INSERT INTO `place_comment` VALUES (161, 'd9166eaa408ba4d4acee7a14', '被go辣的外观和颜色吸引过去的，红色系的装修，很是吸引眼球。就在杭州大厦501负一层，离地铁口蛮近的，店面不算大。<br>点了鲜椒紫苏烤鱼，加了份牛蛙，然后一个经典锅串串。<br>卖相很诱人，也很入味，我个人觉得紫苏口味咸辣控制的很好，紫苏本身自带的香气，配上鲜艳的鲜椒，提升颜值的同时，也增加了牛蛙和烤鱼的口感。<br>牛蛙很嫩，朋友从来不吃牛蛙，但是这次尝试了下后一下爱上了。', '[{\"pic_url\":\"http://qcloud.dpfile.com/pc/KduyUJHD_6l_hMbZfNYRfX-wo05XQFl7QVFIcGWNM5AhuSekESNA004tSK8JcGpoTYGVDmosZWTLal1WbWRW3A.jpg\",\"big_photo\":\"http://qcloud.dpfile.com/pc/KduyUJHD_6l_hMbZfNYRfX-wo05XQFl7QVFIcGWNM5AhuSekESNA004tSK8JcGpoTYGVDmosZWTLal1WbWRW3A.jpg\",\"pic_pv\":0},{\"pic_url\":\"http://qcloud.dpfile.com/pc/s5oSfE99YDLBr5XIgrQ0wJvYDNFhJD3w7EA7v0Cn6uhoiBktvQII5H1EHxzyMYbmTYGVDmosZWTLal1WbWRW3A.jpg\",\"big_photo\":\"http://qcloud.dpfile.com/pc/s5oSfE99YDLBr5XIgrQ0wJvYDNFhJD3w7EA7v0Cn6uhoiBktvQII5H1EHxzyMYbmTYGVDmosZWTLal1WbWRW3A.jpg\",\"pic_pv\":0},{\"pic_url\":\"http://qcloud.dpfile.com/pc/KWajyL4ilGhYL2z0M4Yh2KZ0j8PMnU-ORN_4cCBQXlOTfv6TluQIfNN2hTAqQ18vTYGVDmosZWTLal1WbWRW3A.jpg\",\"big_photo\":\"http://qcloud.dpfile.com/pc/KWajyL4ilGhYL2z0M4Yh2KZ0j8PMnU-ORN_4cCBQXlOTfv6TluQIfNN2hTAqQ18vTYGVDmosZWTLal1WbWRW3A.jpg\",\"pic_pv\":0},{\"pic_url\":\"http://qcloud.dpfile.com/pc/hd3_c4EXvzN9tFIWJ2sDyetvRx7rBdVoSABCd0rrxjSCIvZDD0rQk4Nx6zIwxS9eTYGVDmosZWTLal1WbWRW3A.jpg\",\"big_photo\":\"http://qcloud.dpfile.com/pc/hd3_c4EXvzN9tFIWJ2sDyetvRx7rBdVoSABCd0rrxjSCIvZDD0rQk4Nx6zIwxS9eTYGVDmosZWTLal1WbWRW3A.jpg\",\"pic_pv\":0}]', 0, '2018-06-15 00:00', NULL, '苏小涵sylvia', 'https://p1.meituan.net/userheadpicbackend/de063d22007d4b48906b82327dcbfbe9530664.jpg%40120w_120h_1e_1c_1l%7Cwatermark%3D0', 5.00, 1);
