INSERT INTO `place` VALUES (107, '古庆春门', '环城东路132号', 'null', '2e64ef686ee2feae5b10936d', '风景区', '223人访问', 'http://att2.citysbs.com/hangzhou/image/day_080226/middle_20080226_74c7f390e3ba09652e698DQmhRLfbje0.jpg', NULL, NULL, '暂无', '[]', '[{\"overall_rating\":5,\"date\":\"2018-04-20 00:00\",\"one_url\":\"http://www.dianping.com/shop/17320388/review_all\",\"service_rating\":\"\",\"user_url\":\"http://www.dianping.com/member/23507836\",\"user_url_mobile\":null,\"poi_id\":\"470119242343676612\",\"user_name\":\"张小凡09\",\"uniqkey\":\"6f802674869b05947facbd6ed0bf7d3c\",\"user_logo\":\"https://p1.meituan.net/userheadpicbackend/863554f9c35bb4cd729e1b22b4469d2b428011.jpg%40120w_120h_1e_1c_1l%7Cwatermark%3D0\",\"time_stamp\":1524153600,\"one_url_mobile\":\"http://www.dianping.com/shop/17320388/review_all\",\"price\":null,\"content\":\"古庆春门是杭州城里古十大城门之一，始建于隋唐时期，据说，明初朱元璋部将常遇春率部由此入城，为祝大捷，改称庆春门<br>位置在环城东路与庆春路交叉口,原来的城门早在1959年建环城东路时拆除了，1994年在门址上筑庆春立交桥，现在看到的是2006年原址上仿古建造了一个城门,以杭州清代古城墙为原型，城墙全长66米、宽5.4米、高6.65米，两端为残墙，城墙中间建有城门，城墙里面还有个古城墙陈列馆，不过也早就大门紧闭不开放了<br>下午烈日当头，好多大伯们在城墙拱门洞里打着牌下着棋，热热闹闹的很不错。  \",\"cmt_icons\":{\"comment_icon\":[]},\"video_url\":null,\"video_pic\":null,\"video_time\":null,\"media\":0,\"comment_num\":2,\"src\":\"new_dianping\",\"former_source\":\"new_dianping\",\"cn_name\":\"大众点评\",\"from\":\"dianping.com\",\"comment_url\":\"http://www.dianping.com/shop/17320388/review_all\",\"comment_url_mobile\":\"http://www.dianping.com/shop/17320388/review_all\",\"uid\":\"470119242343676612:new_dianping\",\"mark_source\":20,\"mark_duration\":10,\"mark_multimedia\":0,\"mark_content\":35,\"mark_userscore\":10,\"mark_total\":75,\"cmt_id\":\"6f802674869b05947facbd6ed0bf7d3c\",\"isAgree\":null,\"favorNum\":0,\"reply\":null},{\"overall_rating\":4,\"date\":\"2018-04-03 00:00\",\"one_url\":\"http://www.dianping.com/shop/17320388/review_all\",\"service_rating\":\"\",\"user_url\":\"http://www.dianping.com/member/54575141\",\"user_url_mobile\":null,\"poi_id\":\"470119242343676612\",\"user_name\":\"能美酱好吃\",\"uniqkey\":\"33149a180d0dbab008352f29a8a0dcf9\",\"user_logo\":\"https://p0.meituan.net/userheadpicbackend/2c08a2e1489fa4be6d7154d1d3c27f9a243100.jpg%40120w_120h_1e_1c_1l%7Cwatermark%3D0\",\"time_stamp\":1522684800,\"one_url_mobile\":\"http://www.dianping.com/shop/17320388/review_all\",\"price\":null,\"content\":\"始建于南宋高宗绍兴年间。它有着好几个名字，诸如“东青门”、“菜市门”和“太平门”。 “东青门”是庆春门始建时的名字。“菜市门”的由来是因为门外有菜市。而“太平门”则是元末张士诚改筑杭城之后的俗称，庆春门之称始于明朝。由于杭州东面城垣几次扩大、故地址亦多有变迁。庆春门内的庆春街，历来为繁华街道之一。惠济桥位于庆春门的西面，俗称 “盐桥”，是宋时盐船待榷（卖）处”。东面则有菜市桥，因宋时的蔬菜集市而得名。北面有潮鸣寺，是始建于五代后梁的古刹。寺北有回龙桥，据说是因为高宗赵构曾路过这里题诗而致。庆春门一带，历来就是文人的寓居之地。如唐朝的著名的书法家褚遂良，以《长生殿》留名于世的清代剧作家洪升，都曾在此一带居住。旧时，庆春街的西端，还留存有纪念岳飞的“忠烈祠”。现在古庆春门只剩下城门遗址，也均有铁门拦住不能攀爬，城门脚下，多得是贩卖古玩的商人。  \",\"cmt_icons\":{\"comment_icon\":[]},\"video_url\":null,\"video_pic\":null,\"video_time\":null,\"media\":0,\"comment_num\":2,\"src\":\"new_dianping\",\"former_source\":\"new_dianping\",\"cn_name\":\"大众点评\",\"from\":\"dianping.com\",\"comment_url\":\"http://www.dianping.com/shop/17320388/review_all\",\"comment_url_mobile\":\"http://www.dianping.com/shop/17320388/review_all\",\"uid\":\"470119242343676612:new_dianping\",\"mark_source\":20,\"mark_duration\":10,\"mark_multimedia\":0,\"mark_content\":35,\"mark_userscore\":10,\"mark_total\":75,\"cmt_id\":\"33149a180d0dbab008352f29a8a0dcf9\",\"isAgree\":null,\"favorNum\":0,\"reply\":null}]', 2, 'https://mapsv0.bdimg.com/?qt=pr3dpoi&uid=2e64ef686ee2feae5b10936d&width=720&height=360&quality=80&fovx=120;http://att2.citysbs.com/hangzhou/image/day_080228/middle_20080228_8fd66e52cafb2198d5aaMMl1MF0miY0h.jpg;http://t11.baidu.com/it/u=3201882226,140008280&fm=22', '暂无', 30.25813816449000000000, 120.18781032115000000000, 'false', 4.50, '暂无', '暂无', NULL, NULL, 1);
