INSERT INTO `place` VALUES (123, '万事利丝绸博物馆', '杭州市江干区天城路68号万事利科技大厦B座3层', '(0571)85890639', 'fb70881689f982e48bdb7369', '博物馆', '环境不错;', 'http://hiphotos.baidu.com/map/pic/item/d53f8794a4c27d1e46aa3cac17d5ad6edcc438cd.jpg', NULL, NULL, '9:00-17:00', '[[{\"count\":1,\"label_id\":\"scope_1_01\",\"label_name\":\"环境很好\"}]]', '[{\"overall_rating\":5,\"date\":\"2017-06-29 00:00\",\"one_url\":\"http://m.dianping.com/review/355399413\",\"service_rating\":\"\",\"user_url\":\"\",\"user_url_mobile\":null,\"cmt_icons\":{\"comment_icon\":[]},\"user_name\":\"彼岸的小艾\",\"uniqkey\":\"2fb75db42a10df7793fac1b98ab31135\",\"user_logo\":\"http://store.is.autonavi.com/showpic/b87a2b91ce7c29eb4aa7b32acccd7312\",\"time_stamp\":1498665600,\"one_url_mobile\":\"http://m.dianping.com/review/355399413\",\"price\":null,\"content\":\"因为在杭州的旅游问询处领到了G20旅游护照，对这里很感兴趣，决定来参观一下。万事利大厦B座的一层是产品展示售卖大厅，评凭护照购物可以有不少优惠。博物馆在三层，从一层大厅乘电梯就可以到了。万事利集团为各种大型活动设计的丝绸制品，以及丝绸产业的历史分类都介绍得非常细致，看得出布展的用心，出口处还有一个丝绸之路主题邮局。\",\"pics\":[{\"pic_url\":\"http://store.is.autonavi.com/showpic/f000c52c62669610153194353505ceaf\",\"pic_pv\":32},{\"pic_url\":\"http://store.is.autonavi.com/showpic/48271987bf86cb9f4126188329d81099\",\"pic_pv\":29},{\"pic_url\":\"http://store.is.autonavi.com/showpic/ca5761edb79618c3ee5f05f471671ec9\",\"pic_pv\":16},{\"pic_url\":\"http://store.is.autonavi.com/showpic/a820fd398a6e2b4adc70db3ee37fa71b\",\"pic_pv\":14}],\"video_url\":null,\"video_pic\":null,\"video_time\":null,\"media\":1,\"poi_id\":\"2614546279468269068\",\"src\":\"new_dianping\",\"former_source\":\"new_dianping\",\"cn_name\":\"大众点评\",\"from\":\"dianping.com\",\"comment_url\":\"http://www.dianping.com/shop/58937724/review_all\",\"comment_url_mobile\":\"http://www.dianping.com/shop/58937724/review_all\",\"uid\":\"2614546279468269068:new_dianping\",\"mark_source\":20,\"mark_duration\":5,\"mark_multimedia\":60,\"mark_content\":35,\"mark_userscore\":10,\"mark_total\":130,\"comment_num\":5,\"cmt_id\":\"2fb75db42a10df7793fac1b98ab31135\",\"isAgree\":null,\"