INSERT INTO `place_comment` VALUES (172, '2089583e646dc5ba14b54b58', '20180218礼拜天，下雨。事先了解到，主日早上有两堂，家里人也有安排，所以想赶第一堂，六点多出门，只是没想到杭州的公交车班次不多，等车的时间比车开的时间要多许多。到达要将近八点了。但似乎礼拜刚开始程序，直到第二堂开始，才明白，原来这里的礼拜前半个小时是类似我们青年聚会的敬拜赞美，然后才是唱诗，读经，讲道，祝福等。两堂的信息内容不同。每个月出一期报告单上有教会聚会的时间安排，主日第一次信息分享讲章，每个月讲员的安排。硬件设施很好，聚会也比较兴旺。值得一去。', '[{\"pic_url\":\"http://qcloud.dpfile.com/pc/7uyOZ7mngA1v0NiswKHNUe2kNkLfUT3tkg__BOi0REa02gP6n_pP8IiNC4YjV8AhTYGVDmosZWTLal1WbWRW3A.jpg\",\"pic_pv\":85},{\"pic_url\":\"http://qcloud.dpfile.com/pc/CrVadRPDE4sI6JwORwmyuVG-KryHMVsf6yidMZyBviC7i7vV9ObJPOsO43b14yI-TYGVDmosZWTLal1WbWRW3A.jpg\",\"pic_pv\":85},{\"pic_url\":\"http://qcloud.dpfile.com/pc/xoyxhKMFcnoEXcdLn8R14W7IUHUy9zPCcyKidkcGYjxg6j9t0CW1LrTJzdCeGhEmTYGVDmosZWTLal1WbWRW3A.jpg\",\"pic_pv\":92},{\"pic_url\":\"http://qcloud.dpfile.com/pc/Ca9mfVnzu2KKR39ITLiOE77Cgf85N-EK-ElSUin_MBM0GUjvJzQzFwFs1fXpEp90TYGVDmosZWTLal1WbWRW3A.jpg\",\"pic_pv\":76},{\"pic_url\":\"http://qcloud.dpfile.com/pc/vmAPR2ZdRyAXWVWz8wfciTkraqKBEnk7V3pW6MbePpjjnhhjdeoakUCXE8V2yzfATYGVDmosZWTLal1WbWRW3A.jpg\",\"pic_pv\":68},{\"pic_url\":\"http://qcloud.dpfile.com/pc/bVWpG5iBFxfH4UkLedoKtHEzPL6Phy04KTVgdFQi7rYXKNwzfgiuxdswZL2PI7n0TYGVDmosZWTLal1WbWRW3A.jpg\",\"pic_pv\":66},{\"pic_url\":\"http://qcloud.dpfile.com/pc/T74w6jeI6uukXGcv8chmNHAJmW94naW7LxopdbmbpdD0kAawvhDXOXBa2ZnI1FIWTYGVDmosZWTLal1WbWRW3A.jpg\",\"pic_pv\":60},{\"pic_url\":\"http://qcloud.dpfile.com/pc/AoNUW9beO2lZDLgjxajzs_ebweGgCYfMJbC0afpGCQ39FiMxfamhzOpBpUB36ZKETYGVDmosZWTLal1WbWRW3A.jpg\",\"pic_pv\":73},{\"pic_url\":\"http://qcloud.dpfile.com/pc/evktjPm63CefrNzkSWOs0ThPrpv2POdLIJw02PRHSbwkNVqbowOSz6WrD4VBGHSxTYGVDmosZWTLal1WbWRW3A.jpg\",\"pic_pv\":50},{\"pic_url\":\"http://qcloud.dpfile.com/pc/cX-p39d-RhWiQSN4NQ5iN7enKhuTS1qTBQ19lnJ-BwX0kAawvhDXOXBa2ZnI1FIWTYGVDmosZWTLal1WbWRW3A.jpg\",\"pic_pv\":37},{\"pic_url\":\"http://qcloud.dpfile.com/pc/2TWMJeN7uQzS4Prz6SExtHt9GOj8heBRmbrAupy1EU0iwrBb5QDLn8whC_rzgH4hTYGVDmosZWTLal1WbWRW3A.jpg\",\"pic_pv\":3},{\"pic_url\":\"http://qcloud.dpfil