INSERT INTO `place_comment` VALUES (250, '7f77c66bb5622043c7f02511', '酒香不怕巷子深，地方很难找，不过还是每天都有很多人排队，两个人点了四个菜，足够吃了<br>熟醉沼虾，这个我最爱，从来没吃过的味道，很香还有酒的味道，虾的肉质也很紧实，虾头还有黄<br>土烧黄鱼年糕，本来想点目鱼仔烧仔排的，结果说目鱼仔没了，年糕还不错，黄鱼有点不够入味<br>特色自制豆腐，汤烧的还不错，豆腐也挺嫩的<br>铁板香芋，比较喜欢这种饱饱的食物，味道还可以，小心烫哦  ', '[{\"pic_url\":\"http://qcloud.dpfile.com/pc/Fb-lj9Ram3e5_hx6MjuUu6IF9v13OFyoNvwWj3A2MroaWV7jnGXDu6q3ZNCC13ooTYGVDmosZWTLal1WbWRW3A.jpg\",\"big_photo\":\"http://qcloud.dpfile.com/pc/Fb-lj9Ram3e5_hx6MjuUu6IF9v13OFyoNvwWj3A2MroaWV7jnGXDu6q3ZNCC13ooTYGVDmosZWTLal1WbWRW3A.jpg\",\"pic_pv\":20},{\"pic_url\":\"http://qcloud.dpfile.com/pc/XEv-pB8hp6RnlwvuYJrHrn-uTkPk6bcDH3_8NBwB6wvRKFpYEo6H3OpKaU98Sy8-TYGVDmosZWTLal1WbWRW3A.jpg\",\"big_photo\":\"http://qcloud.dpfile.com/pc/XEv-pB8hp6RnlwvuYJrHrn-uTkPk6bcDH3_8NBwB6wvRKFpYEo6H3OpKaU98Sy8-TYGVDmosZWTLal1WbWRW3A.jpg\",\"pic_pv\":19},{\"pic_url\":\"http://qcloud.dpfile.com/pc/Ei8_45QGliU1KYFSrLuQchUfAN55B9_3XCnGh2kOajBWzN_4hUPhHPENFzwE2h71TYGVDmosZWTLal1WbWRW3A.jpg\",\"big_photo\":\"http://qcloud.dpfile.com/pc/Ei8_45QGliU1KYFSrLuQchUfAN55B9_3XCnGh2kOajBWzN_4hUPhHPENFzwE2h71TYGVDmosZWTLal1WbWRW3A.jpg\",\"pic_pv\":2},{\"pic_url\":\"http://qcloud.dpfile.com/pc/WAPyhXHU9hYzcbM5V2SfqL7EFkOwiqYDhsukjkLR-vO-veYWhBs4hEoHF521uEMcTYGVDmosZWTLal1WbWRW3A.jpg\",\"big_photo\":\"http://qcloud.dpfile.com/pc/WAPyhXHU9hYzcbM5V2SfqL7EFkOwiqYDhsukjkLR-vO-veYWhBs4hEoHF521uEMcTYGVDmosZWTLal1WbWRW3A.jpg\",\"pic_pv\":5}]', 0, '2018-06-21 00:00', NULL, '我是娇大人', 'https://p0.meituan.net/userheadpicbackend/9930524a26d324215607744dc49c0b6e49618.jpg%40120w_120h_1e_1c_1l%7Cwatermark%3D0', 5.00, 1);
