INSERT INTO `place_comment` VALUES (253, 'd991e1163749864c1e38949f', '环境很好很清静，单独包厢私密性很好，很适合请客中午聚餐用了团购套餐，比较划算香芒脆皮鸡:芒果不是很甜，鸡皮很脆,中间还夹了锅巴，脆脆的很好吃广式笋壳鱼:不太吃鱼的人难得吃鱼，鱼很新鲜,烧的味道也不错南瓜粥:里面的黑米煮的很烂很糯，再加上薏米和南瓜的清香，也适合当小朋友的辅食盐焗虾:有点类似虾干,很有嚼劲,味淡保持虾干原味  ', '[{\"pic_url\":\"http://qcloud.dpfile.com/pc/T2yqfV-1cKTGoOWPh1sVFvMaQNboVBVGm3HmtPv8zf1m2cIggjFEH_RcDG1HWAsVTYGVDmosZWTLal1WbWRW3A.jpg\",\"big_photo\":\"http://qcloud.dpfile.com/pc/T2yqfV-1cKTGoOWPh1sVFvMaQNboVBVGm3HmtPv8zf1m2cIggjFEH_RcDG1HWAsVTYGVDmosZWTLal1WbWRW3A.jpg\",\"pic_pv\":23},{\"pic_url\":\"http://qcloud.dpfile.com/pc/1OFSDGvh16RhtRF_lwVM92btr4lPJtCR3FlxWiYPeD16lo_kpsFhbNx1yHyVDqGrTYGVDmosZWTLal1WbWRW3A.jpg\",\"big_photo\":\"http://qcloud.dpfile.com/pc/1OFSDGvh16RhtRF_lwVM92btr4lPJtCR3FlxWiYPeD16lo_kpsFhbNx1yHyVDqGrTYGVDmosZWTLal1WbWRW3A.jpg\",\"pic_pv\":23},{\"pic_url\":\"http://qcloud.dpfile.com/pc/KYYUmfeHEK1nzf-wKH16JR_N-rIOxTJivltL5-pNmgxxhwTPxxcbal6SR_KLdMFeTYGVDmosZWTLal1WbWRW3A.jpg\",\"big_photo\":\"http://qcloud.dpfile.com/pc/KYYUmfeHEK1nzf-wKH16JR_N-rIOxTJivltL5-pNmgxxhwTPxxcbal6SR_KLdMFeTYGVDmosZWTLal1WbWRW3A.jpg\",\"pic_pv\":25},{\"pic_url\":\"http://qcloud.dpfile.com/pc/zhSf7MvL6PGWzNSRkAmdhUA3x7td8tcaZvcBhY_zkX0huSekESNA004tSK8JcGpoTYGVDmosZWTLal1WbWRW3A.jpg\",\"big_photo\":\"http://qcloud.dpfile.com/pc/zhSf7MvL6PGWzNSRkAmdhUA3x7td8tcaZvcBhY_zkX0huSekESNA004tSK8JcGpoTYGVDmosZWTLal1WbWRW3A.jpg\",\"pic_pv\":24},{\"pic_url\":\"http://qcloud.dpfile.com/pc/cmA1tO4Ex_Sp65Lszbo7OasHJ0wex8SqJslBQMYtVKQj5lz1eDFkcCiBcYKi1L8pTYGVDmosZWTLal1WbWRW3A.jpg\",\"big_photo\":\"http://qcloud.dpfile.com/pc/cmA1tO4Ex_Sp65Lszbo7OasHJ0wex8SqJslBQMYtVKQj5lz1eDFkcCiBcYKi1L8pTYGVDmosZWTLal1WbWRW3A.jpg\",\"pic_pv\":1},{\"pic_url\":\"http://qcloud.dpfile.com/pc/l_MpUpMCt2I2vZAD1fHgnDPnFOPYmd6oIXOCjmo20Tdzi8T-Y6YXf4kATIVdORdQTYGVDmosZWTLal1WbWRW3A.jpg\",\"big_photo\":\"http://qcloud.dpfile.com/pc/l_MpUpMCt2I2vZAD1fHgnDPnFOPYmd6oIXOCjmo20Tdzi8T-Y6YXf4kATIVdORdQTYGVDmosZWTLal1WbWRW3A.jpg\",\"pic_pv\":3}]', 0, '2018-03-20 13:16', NULL, 'Ada