INSERT INTO `place_comment` VALUES (259, '6cdf5d425bb7c9b349fc8d37', '晚上8:00多看到这家餐厅还比较热闹，就过去吃晚饭，这家好像是以吃小龙虾为主的，但是吃杭帮菜的话也还比较正宗。这家菜分量还不错，价格也还比较实惠，这次带爸妈一起吃饭，三个人点了六个菜，吃到撑。小米糕非常好吃，喜欢吃糕点的千万不要错过。超出预期的是，西湖牛肉羹非常好喝，我们三个人把一盆汤喝光了。酸汤肥牛比较中规中矩，稍微有点辣。糖醋小排炸得稍微有点老，味道一般。', '[{\"pic_url\":\"http://qcloud.dpfile.com/pc/IUFKVLneyGvbFJ3lQVzmSw7FNR6cYS1yD8hhIuXQ1fKfCk7DH6z8B4ncORz8P0FjjoJrvItByyS4HHaWdXyO_DrXIaWutJls2xCVbatkhjUNNiIYVnHvzugZCuBITtvjski7YaLlHpkrQUr5euoQrg.jpg\",\"big_photo\":\"http://qcloud.dpfile.com/pc/IUFKVLneyGvbFJ3lQVzmSw7FNR6cYS1yD8hhIuXQ1fKfCk7DH6z8B4ncORz8P0FjjoJrvItByyS4HHaWdXyO_DrXIaWutJls2xCVbatkhjUNNiIYVnHvzugZCuBITtvjski7YaLlHpkrQUr5euoQrg.jpg\",\"pic_pv\":6},{\"pic_url\":\"http://qcloud.dpfile.com/pc/R39rG9C0QVlOb5KLM2kqavztRYo5pfaBYCddQwPdcM-NJJyLrOQU-SMeLn0eUv76joJrvItByyS4HHaWdXyO_DrXIaWutJls2xCVbatkhjUNNiIYVnHvzugZCuBITtvjski7YaLlHpkrQUr5euoQrg.jpg\",\"big_photo\":\"http://qcloud.dpfile.com/pc/R39rG9C0QVlOb5KLM2kqavztRYo5pfaBYCddQwPdcM-NJJyLrOQU-SMeLn0eUv76joJrvItByyS4HHaWdXyO_DrXIaWutJls2xCVbatkhjUNNiIYVnHvzugZCuBITtvjski7YaLlHpkrQUr5euoQrg.jpg\",\"pic_pv\":6},{\"pic_url\":\"http://qcloud.dpfile.com/pc/fzINqrSIHcHah4-MRrxGWxzMnAz-u1gNH3KFCVgWElV6lo_kpsFhbNx1yHyVDqGrjoJrvItByyS4HHaWdXyO_DrXIaWutJls2xCVbatkhjUNNiIYVnHvzugZCuBITtvjski7YaLlHpkrQUr5euoQrg.jpg\",\"big_photo\":\"http://qcloud.dpfile.com/pc/fzINqrSIHcHah4-MRrxGWxzMnAz-u1gNH3KFCVgWElV6lo_kpsFhbNx1yHyVDqGrjoJrvItByyS4HHaWdXyO_DrXIaWutJls2xCVbatkhjUNNiIYVnHvzugZCuBITtvjski7YaLlHpkrQUr5euoQrg.jpg\",\"pic_pv\":7},{\"pic_url\":\"http://qcloud.dpfile.com/pc/J8nxBwYbyrwvo2ceQEKRxw42N3nYmcp_f4z6SZq1LcnNRkPF4P-002uboxXAK6aSjoJrvItByyS4HHaWdXyO_DrXIaWutJls2xCVbatkhjUNNiIYVnHvzugZCuBITtvjski7YaLlHpkrQUr5euoQrg.jpg\",\"big_photo\":\"http://qcloud.dpfile.com/pc/J8nxBwYbyrwvo2ceQEKRxw42N3nYmcp_f4z6SZq1LcnNRkPF4P-002uboxXAK6aSjoJrvItByyS4HHaWdXyO_DrXIaWutJls2xCVbatkhjUNNiIYVnHvzugZCuBITtvjski7YaLlHpkrQUr5euoQrg.jpg\",\"pic_pv\":0},{\"pic_url\":\"http://qcloud.dpfile.com/pc/Ld9ChocReJUZKP59V1vtQqKwFU0ICVvYhoh_WuKhE