INSERT INTO `photo_distinguish` VALUES (8, 'oE9el5Hx6LplsHe2roGhrkxkEUsk', '/images/154777636403934847996_src.jpg', '{\"result\":[{\"probability\":\"0.324271\",\"has_calorie\":true,\"calorie\":\"-1\",\"name\":\"地狱拉面\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E5%9C%B0%E7%8B%B1%E6%8B%89%E9%9D%A2\\/7614039\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/503d269759ee3d6d12ed53494f166d224f4ade0d.jpg\",\"description\":\"地狱拉面是一种著名的日本拉面。地狱拉面在日本拉面中可以算是一种比较辣的拉面，它选用新鲜豚骨、鸡骨熬制成汤底，爽口、劲道的拉面上铺着辣椒、紫菜、叉烧和豆芽，入口先是浓浓的醇香，然后舌头会渐渐感到一丝辣意，但决不会让人辣到流下眼泪。\"}},{\"probability\":\"0.309891\",\"has_calorie\":true,\"calorie\":\"491\",\"name\":\"豚骨拉面\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E8%B1%9A%E9%AA%A8%E6%8B%89%E9%9D%A2\\/4632824\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/d6ca7bcb0a46f21ff09287fcfc246b600d33aeff.jpg\",\"description\":\"豚骨拉面是一道食物，制作原料主要有豚骨高汤、肉片、细拉面、罐头玉米粒等，口味鲜美，营养丰富。这也是《西游记》中沙悟净爱吃的料理。\"}},{\"probability\":\"0.0508439\",\"has_calorie\":true,\"calorie\":\"-1\",\"name\":\"叉烧面\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E5%8F%89%E7%83%A7%E9%9D%A2\\/12984977\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/09fa513d269759ee465ae3a7b0fb43166d22df50.jpg\",\"description\":\"叉烧面是一道美食，制作原料主要有叉烧、面条、葱、鸡蛋。\"}},{\"probability\":\"0.041325\",\"has_calorie\":true,\"calorie\":\"82\",\"name\":\"味千拉面\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E5%91%B3%E5%8D%83%E6%8B%89%E9%9D%A2\\/10696989\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/0823dd54564e9258cb143b639e82d158ccbf4e1e.jpg\",\"description\":\"味千拉面徽号是妹妹携带碗面图案，在日本已有25余间分店(其中7成在熊本县)。1996年潘慰开始将日本味千拉面引入香港及内地，并先后取得该品牌在香港、澳门及内地的永久经营权。2007年3月，味千拉面的股票在香港主板上市。2018年11月27日，味千拉面在上海中心举办“大骨熬汤50年，全球门店800家”品牌战略升级发布会。 味千骨汤是采用日本中央大厨房熬汤工艺，生成了高品质的味千骨汤汤底。从选材、切块、熬煮、蒸发、包装、配送、出品七大核心环节，通过一体化的品控管理，对时间、温度、计量、顺序等进行把控，也正是因为中央大厨房的熬汤工艺保证了标准化出品，让口味一致的骨汤送到不同门店。2018年11月17日，国内多家媒体参观了味千位于山形的中央大厨房，据相关负责人介绍，中央大厨房熬汤已成为日本连锁拉面店主流的熬汤方式。针对此前消息质疑的钙质含量问题，味千方面表示，“味千汤底的钙含量是由于浓缩未还原的计算方式错误而致，对此我们已经修改。”11月上海市工商局在日前以反不正当竞争法为由，决定对味千进行行政处罚，罚款20万元。2012年2月，味千拉面被曝产品提价，公司方面回应称提价已有半年以上时间。\"}},{\"probability\":\"0.0381702\",\"has_calorie\":true,\"calorie\":\"146\",\"name\":\"炒乌冬面\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E7%82%92%E4%B9%8C%E5%86%AC%E9%9D%A2\\/6645196\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/42166d224f4a20a4e2ba85329a529822730ed09b.jpg\",\"description\":\"炒乌冬面是一道美味的日式菜肴，制作原料有青椒，猪肉，洋葱等。制作简单，口感颇佳，营养丰富，食材可根据自己口味添加或减少。\"}}],\"log_id\":5655847124047368626,\"result_num\":5}', '2019-01-18 09:52:45', '4');
