CREATE TABLE `translator`  (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `user_id` int(11) NOT NULL COMMENT '用户id',
  `text_id` int(11) NOT NULL COMMENT '文本来源表中的id',
