INSERT INTO `photo_distinguish` VALUES (71, 'oE9el5Hx6LplsHe2roGhrkxkEUsk', '/images/photo/155256516476853075742_src.jpg', '{\"result\":[{\"probability\":\"0.324271\",\"has_calorie\":true,\"calorie\":\"-1\",\"name\":\"地狱拉面\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E5%9C%B0%E7%8B%B1%E6%8B%89%E9%9D%A2\\/7614039\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/503d269759ee3d6d12ed53494f166d224f4ade0d.jpg\",\"description\":\"地狱拉面是一种著名的日本拉面。地狱拉面在日本拉面中可以算是一种比较辣的拉面，它选用新鲜豚骨、鸡骨熬制成汤底，爽口、劲道的拉面上铺着辣椒、紫菜、叉烧和豆芽，入口先是浓浓的醇香，然后舌头会渐渐感到一丝辣意，但决不会让人辣到流下眼泪。\"}},{\"probability\":\"0.309891\",\"has_calorie\":true,\"calorie\":\"491\",\"name\":\"豚骨拉面\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E8%B1%9A%E9%AA%A8%E6%8B%89%E9%9D%A2\\/4632824\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/d6ca7bcb0a46f21ff09287fcfc246b600d33aeff.jpg\",\"description\":\"豚骨拉面是一道食物，制作原料主要有豚骨高汤、肉片、细拉面、罐头玉米粒等，口味鲜美，营养丰富。这也是《西游记》中沙悟净爱吃的料理。\"}},{\"probability\":\"0.0508439\",\"has_calorie\":true,\"calorie\":\"-1\",\"name\":\"叉烧面\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E5%8F%89%E7%83%A7%E9%9D%A2\\/12984977\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/09fa513d269759ee465ae3a7b0fb43166d22df50.jpg\",\"description\":\"叉烧面是一道美食，制作原料主要有叉烧、面条、葱、鸡蛋。\"}},{\"probability\":\"0.041325\",\"has_calorie\":true,\"calorie\":\"82\",\"name\":\"味千拉面\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E5%91%B3%E5%8D%83%E6%8B%89%E9%9D%A2\\/10696989\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/0823dd54564e9258cb143b639e82d158ccbf4e1e.jpg\",\"description\":\"味千拉面徽号是妹妹携带碗面图案，在日本已有25余间分店(其中7成在熊本县)。1968年创始于日本。1996年潘慰开始将日本味千拉面引入香港及内地，并先后取得该品牌在香港、澳门及内地的永久经营权。2007年3月，味千拉面的股票在香港主板上市。2018年11月27日，味千拉面在上海中心举办“大骨熬汤50年，全球门店800家”品牌战略升级发布会。 味千骨汤是采用日本中央大厨房熬汤工艺，生成了高品质的味千骨汤汤底。从选材、切块、熬煮、蒸发、包装、配送、出品七大核心环节，通过一体化的品控管理，对时间、温度、计量、顺序等进行把控，也正是因为中央大厨房的熬汤工艺保证了标准化出品，让口味一致的骨汤送到不同门店。2018年11月17日，国内多家媒体参观了味千位于山形的中央大厨房，据相关负责人介绍，中央大厨房熬汤已成为日本连锁拉面店主流的熬汤方式。针对此前消息质疑的钙质含量问题，味千方面表示，“味千汤底的钙含量是由于浓缩未还原的计算方式错误而致，对此我们已经修改。”11月上海市工商局在日前以反不正当竞争法为由，决定对味千进行行政处罚，罚款20万元。2012年2月，味千拉面被曝产品提价，公司方面回应称提价已有半年以上时间。\"}},{\"probability\":\"0.0381702\",\"has_calorie\":true,\"calorie\":\"146\",\"name\":\"炒乌冬面\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E7%82%92%E4%B9%8C%E5%86%AC%E9%9D%A2\\/6645196\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/42166d224f4a20a4e2ba85329a529822730ed09b.jpg\",\"description\":\"炒乌冬面是一道美味的日式菜肴，制作原料有青椒，猪肉，洋葱等。制作简单，口感颇佳，营养丰富，食材可根据自己口味添加或减少。\"}}],\"log_id\":7144495755206867438,\"result_num\":5}', '2019-03-14 20:06:05', '4', '杭州市·江干区');
INSERT INTO `photo_distinguish` VALUES (72, 'oE9el5Hx6LplsHe2roGhrkxkEUsk', '/images/photo/155256517429180303673_src.jpg', '{\"result\":[{\"score\":0.895516,\"root\":\"自然风景-山峦\",\"keyword\":\"山峦\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E5%B1%B1%E5%B3%A6\\/6971676\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/574e9258d109b3dec73c996ecfbf6c81800a4c30.jpg\",\"description\":\"山：高度较大，坡度较陡的高地，峦：小而尖的山，或连绵的山。山峦，指相连的高山。\"}},{\"score\":0.65381,\"root\":\"自然风景-天空\",\"keyword\":\"天空\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E5%A4%A9%E7%A9%BA\\/5534\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/b8014a90f603738d6d3004d1b61bb051f919eccf.jpg\",\"description\":\"天空是地球重要的组成部分，在古代中国有天圆地方的说法谓天际空阔。 唐 沈佺期 《绍隆寺》诗：“云盖看木秀，天空见藤盘。” 前蜀 贯休 《送僧归天台寺》诗：“天空闻圣磬，瀑细落花巾。” 2.日月星辰罗列的广大空间。 唐 韩愈 《南山》诗：“天空浮脩眉，浓绿画新就。” 茅盾 《秋收》：“偶尔薄暮时分天空有几片白云，全村的人都欢呼起来。”人可以在天空观测气象或天文现象，从而得知天气变化、时间的流逝或自己的方位。日出日落可知一日中的时间，晚上月亮的盈亏可以知道一个月的时间。北斗星可以指示北方。云的厚度和形状可以知道会否下雨。 在天空可以欣赏到许多美丽的现象，如彩虹、极光和流星雨等。雀鸟会在天空飞翔。 由于石油等化石性燃料使用的增加而产生的悬浮质，特别是那些会在燃烧后释放二氧化硫的煤等燃料的影响，自1973年以来，除了欧洲，天空的能见度正在逐步降低。\"}},{\"score\":0.406492,\"root\":\"自然风景-湖泊\",\"keyword\":\"湖泊\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E6%B9%96%E6%B3%8A\\/242936\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/8435e5dde71190ef635cd56ccd1b9d16fcfa60d9.jpg\",\"description\":\"湖盆及其承纳的水体。湖盆是地表相对封闭可蓄水的天然洼池。湖泊按成因可分为构造湖、火山口湖、冰川湖、堰塞湖、喀斯特湖、河成湖、风成湖、海成湖和人工湖(水库)等。按泄水情况可分为外流湖(吞吐湖)和内陆湖；按湖水含盐度可分为淡水湖(含盐度小于1g\\/L)、咸水湖(含盐度为1-35g\\/L)和盐湖(含盐度大于35g\\/L)。湖水的来源是降水、地面径流、地下水，有的则来自冰雪融水。湖水的消耗主要是蒸发、渗漏、排泄和开发利用。\"}},{\"score\":0.217902,\"root\":\"自然风景-其他\",\"keyword\":\"风景\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E9%A3%8E%E6%99%AF\\/645\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/574e9258d109b3de52a41f13c6bf6c81800a4cb6.jpg\",\"description\":\"风景指的是供观赏的自然风光、景物，包括自然景观,人文景观。风景是由光对物的反映所显露出来的一种景象。犹言风光,景物、景色等，含义广泛。在中国古书上，尤其纯文艺作品的诗文方面，更是延用已久，甚至写景多于言情，几乎和旅游打成一片。而且还有一大部分作品是借景抒情，寓情于景。\"}},{\"score\":0.013669,\"root\":\"自然风景-冰雪\",\"keyword\":\"冰雪\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E5%86%B0%E9%9B%AA\\/68514\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/0b7b02087bf40ad1e1e56d35552c11dfa8eccee7.jpg\",\"description\":\"冰雪，指冰和雪；比喻心志的忠贞、品格的高尚。语出《后汉书·西羌传论》：“﹝ 段熲 ﹞被羽前登，身当百死之陈，蒙没冰雪，经履千折之道，始殄西种，卒定东寇。”\"}}],\"log_id\":4060165542174070734,\"result_num\":5}', '2019-03-14 20:06:15', '1', '杭州市·江干区');
INSERT INTO `photo_distinguish` VALUES (73, 'oE9el5Hx6LplsHe2roGhrkxkEUsk', '/images/photo/155256825729886835733_src.jpg', '{\"result\":[{\"probability\":\"0.324271\",\"has_calorie\":true,\"calorie\":\"-1\",\"name\":\"地狱拉面\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E5%9C%B0%E7%8B%B1%E6%8B%89%E9%9D%A2\\/7614039\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/503d269759ee3d6d12ed53494f166d224f4ade0d.jpg\",\"description\":\"地狱拉面是一种著名的日本拉面。地狱拉面在日本拉面中可以算是一种比较辣的拉面，它选用新鲜豚骨、鸡骨熬制成汤底，爽口、劲道的拉面上铺着辣椒、紫菜、叉烧和豆芽，入口先是浓浓的醇香，然后舌头会渐渐感到一丝辣意，但决不会让人辣到流下眼泪。\"}},{\"probability\":\"0.309891\",\"has_calorie\":true,\"calorie\":\"491\",\"name\":\"豚骨拉面\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E8%B1%9A%E9%AA%A8%E6%8B%89%E9%9D%A2\\/4632824\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/d6ca7bcb0a46f21ff09287fcfc246b600d33aeff.jpg\",\"description\":\"豚骨拉面是一道食物，制作原料主要有豚骨高汤、肉片、细拉面、罐头玉米粒等，口味鲜美，营养丰富。这也是《西游记》中沙悟净爱吃的料理。\"}},{\"probability\":\"0.0508439\",\"has_calorie\":true,\"calorie\":\"-1\",\"name\":\"叉烧面\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E5%8F%89%E7%83%A7%E9%9D%A2\\/12984977\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/09fa513d269759ee465ae3a7b0fb43166d22df50.jpg\",\"description\":\"叉烧面是一道美食，制作原料主要有叉烧、面条、葱、鸡蛋。\"}},{\"probability\":\"0.041325\",\"has_calorie\":true,\"calorie\":\"82\",\"name\":\"味千拉面\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E5%91%B3%E5%8D%83%E6%8B%89%E9%9D%A2\\/10696989\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/0823dd54564e9258cb143b639e82d158ccbf4e1e.jpg\",\"description\":\"味千拉面徽号是妹妹携带碗面图案，在日本已有25余间分店(其中7成在熊本县)。1968年创始于日本。1996年潘慰开始将日本味千拉面引入香港及内地，并先后取得该品牌在香港、澳门及内地的永久经营权。2007年3月，味千拉面的股票在香港主板上市。2018年11月27日，味千拉面在上海中心举办“大骨熬汤50年，全球门店800家”品牌战略升级发布会。 味千骨汤是采用日本中央大厨房熬汤工艺，生成了高品质的味千骨汤汤底。从选材、切块、熬煮、蒸发、包装、配送、出品七大核心环节，通过一体化的品控管理，对时间、温度、计量、顺序等进行把控，也正是因为中央大厨房的熬汤工艺保证了标准化出品，让口味一致的骨汤送到不同门店。2018年11月17日，国内多家媒体参观了味千位于山形的中央大厨房，据相关负责人介绍，中央大厨房熬汤已成为日本连锁拉面店主流的熬汤方式。针对此前消息质疑的钙质含量问题，味千方面表示，“味千汤底的钙含量是由于浓缩未还原的计算方式错误而致，对此我们已经修改。”11月上海市工商局在日前以反不正当竞争法为由，决定对味千进行行政处罚，罚款20万元。2012年2月，味千拉面被曝产品提价，公司方面回应称提价已有半年以上时间。\"}},{\"probability\":\"0.0381702\",\"has_calorie\":true,\"calorie\":\"146\",\"name\":\"炒乌冬面\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E7%82%92%E4%B9%8C%E5%86%AC%E9%9D%A2\\/6645196\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/42166d224f4a20a4e2ba85329a529822730ed09b.jpg\",\"description\":\"炒乌冬面是一道美味的日式菜肴，制作原料有青椒，猪肉，洋葱等。制作简单，口感颇佳，营养丰富，食材可根据自己口味添加或减少。\"}}],\"log_id\":1630370553927114350,\"result_num\":5}', '2019-03-14 20:57:38', '4', '杭州市·江干区');
INSERT INTO `photo_distinguish` VALUES (74, 'oE9el5Hx6LplsHe2roGhrkxkEUsk', '/images/photo/155256848822398779916_src.jpg', '{\"result\":[{\"score\":0.95120388269424,\"name\":\"苏铁\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E8%8B%8F%E9%93%81\\/323399\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/32fa828ba61ea8d36fd15f93950a304e241f58b1.jpg\",\"description\":\"苏铁。俗称：铁树，别名：辟火蕉、凤尾蕉、凤尾松、凤尾草，拉丁文名：Cycas revoluta Thunb.苏铁科、苏铁属，一说是因其木质密度大，入水即沉，沉重如铁而得名；另一说因其生长需要大量铁元素，故而名之。又名凤尾蕉、避火蕉、凤尾松，多种植在南方，现广泛分布于中国、日本、菲律宾和印度尼西亚等国家。苏铁最为出名的是其开花，被称之为“铁树开花”。苏铁为优美的观赏树种，栽培极为普遍，茎内含淀粉，可供食用；种子含油和丰富的淀粉，微有毒，供食用和药用，有治痢疾、止咳和止血之效。\"}},{\"score\":0.022495571523905,\"name\":\"篦齿苏铁\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E7%AF%A6%E9%BD%BF%E8%8B%8F%E9%93%81\\/170717\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/d52a2834349b033b8484b82d10ce36d3d539bd3d.jpg\",\"description\":\"篦齿苏铁(学名：Cycas pectinata Griff.)：树干圆柱形，高达3米。羽状叶长1.2-1.5米，叶轴横切面圆形或三角状圆形，柄两侧有疏刺，刺略向下弯，羽状裂片80-120对，条形或披针状条形，厚革质，坚硬，上面深绿色，下面绿色。雄球花长圆锥状圆柱形，小孢子叶楔形，大孢子叶密被褐黄色绒毛，上部的顶片斜方状宽圆形或宽圆形，宽较长为大或长宽几相等，宽6-8厘米，边缘有30余枚钻形裂片。上部两侧生胚珠2-4枚，胚珠无毛，卵圆形或近圆球形。种子卵圆形或椭圆状倒卵圆形，熟时暗红褐色，具光泽，干后外种,皮常同中种皮分离开。有栽培，作庭园观赏树用。原产地中国、印度、尼泊尔、缅甸、泰国、柬埔寨、老挝、越南。(概述图片参考资料来源：)\"}},{\"score\":0.021322403103113,\"name\":\"台东苏铁\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E5%8F%B0%E4%B8%9C%E8%8B%8F%E9%93%81\\/1132441\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/29381f30e924b8998e42b9d26e061d950b7bf6da.jpg\",\"description\":\"台东苏铁为棕榈状常绿植物。干圆柱形，高可达5米，干径达45厘米。叶集茎顶，被绒毛。叶柄长10-25厘米，具10-25短刺；羽叶长0.8-1米，羽片100-200对，条形，革质，长15-22厘米。雄球花序狭圆柱形，长30-50厘米；大孢子叶密被褐色绒毛，孢子叶片宽卵形，长10-28厘米，宽7-11厘米，篦齿状分裂。本种仅分布于台湾东部，台东县的红叶溪上游，海拔400-800米有大面积集中分布区。由于种子大，难于传播远处，除在产地保护外，更应加强引种栽培研究。台北中央研究院和深圳仙湖植物园都已引种成功。中国第一批保护植物红皮书中并没有台东苏铁，该种是1994年植物学家对原台湾苏铁(C.taiwaniana)深入研究并核对了模式标本之后确立的新种，过去一直错误地把本种当作台湾苏铁，考虑到它的来历，应该把它与原台湾苏铁同样视作同一级别的保护植物。\"}},{\"score\":0.011128640733659,\"name\":\"攀枝花苏铁\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E6%94%80%E6%9E%9D%E8%8A%B1%E8%8B%8F%E9%93%81\\/801687\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/2fdda3cc7cd98d10610234b82c3fb80e7aec9060.jpg\",\"description\":\"攀枝花苏铁(学名：Cycas panzhihuaensis L.Zhou et S.Y.Yang)是苏铁科苏铁属植物，棕涧状常绿植物，高1-2.5米，茎干通常单一，覆被着宿存的叶柄基部。叶螺旋状排列，簇生于茎干的顶部，羽状全裂，长70-120厘米，叶柄上部两侧有平展的短刺。雌雄异株；小孢子叶球单生茎顶，常偏斜，纺锤状圆柱形或椭圆状圆柱形，通常微弯曲，梗弯曲。种子近球形或微扁，种子倒圆锥状球形或倒卵状圆球形，种皮骨质，平滑。分布于中国四川南部和云南北部。生长于海拔1100-2000米的常绿阔叶林下或稀树灌丛中。该种为中国特有的古老残遗种，在研究植物区系、植物地理、古气候和古地理等方面有重要意义。已广泛引种到华南地区的各类树木园、植物园和物种保存基地，可推广应用于园林绿化、庭院造景及城市环境建设，具有较高的观赏价值。(概述图参考来源：)\"}},{\"score\":0.0067252982407808,\"name\":\"宽叶苏铁\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E5%AE%BD%E5%8F%B6%E8%8B%8F%E9%93%81\\/10216624\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/0d338744ebf81a4ca7fe8f52d72a6059242da659.jpg\",\"description\":\"宽叶苏铁(Cycas balansae)是苏铁属植物，因其叶片宽大而得名，其拉丁学名以发现者法国植物学家Benedict Balansa命名，因此中文名又音译为巴兰萨苏铁。分布于广西南部、云南南部及老挝、缅甸、泰国、越南。宽叶苏铁(Cycas balansae)存在一些分类鉴定的问题。例如，单羽苏铁(C.simplicipinna)和近年来被描述的元江苏铁(C.parvula)与绿春苏铁(C.tanqingii),都暂时地列为其同物异名，可能有作为独立种的价值。宽叶苏铁也曾被错误地鉴定为云南苏铁(C.siamensis Miquel)本种是国家一级保护植物(第一批)，非特有种。\"}}],\"log_id\":1166470450629925774}', '2019-03-14 21:01:30', '2', '杭州市·江干区');
INSERT INTO `photo_distinguish` VALUES (76, 'oE9el5Hx6LplsHe2roGhrkxkEUsk', '/images/photo/155256918599390837023_src.jpg', '{\"result\":[{\"score\":0.498987,\"root\":\"商品-食物\",\"keyword\":\"蛋糕\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E8%9B%8B%E7%B3%95\\/29152\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/64380cd7912397ddfc13a91b5382b2b7d0a28742.jpg\",\"description\":\"蛋糕是一种古老的西点，一般是由烤箱制作的，蛋糕是用鸡蛋、白糖、小麦粉为主要原料。以牛奶、果汁、奶粉、香粉、色拉油、水，起酥油、泡打粉为辅料。经过搅拌、调制、烘烤后制成一种像海绵的点心。蛋糕是一种面食，通常是甜的，典型的蛋糕是以烤的方式制作出来。蛋糕的材料主要包括了面粉、甜味剂(通常是蔗糖)、黏合剂(一般是鸡蛋，素食主义者可用面筋和淀粉代替)、起酥油(一般是牛油或人造牛油，低脂肪含量的蛋糕会以浓缩果汁代替)，液体(牛奶，水或果汁)，香精和发酵剂(例如酵母或者发酵粉)。\"}},{\"score\":0.347805,\"root\":\"非自然图像-图像素材\",\"keyword\":\"包装袋\\/盒\",\"baike_info\":{}},{\"score\":0.237446,\"root\":\"植物-花束\",\"keyword\":\"糖果花束\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E7%B3%96%E6%9E%9C%E8%8A%B1%E6%9D%9F\\/215055\",\"description\":\"糖果花束是当今流行于欧美的一种时尚礼品，由精美的糖果和花束这两种广被世人所接受的礼品概念组成。\"}},{\"score\":0.120679,\"root\":\"植物-花束\",\"keyword\":\"情人节花束\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E6%83%85%E4%BA%BA%E8%8A%82%E8%8A%B1%E6%9D%9F\\/8851910\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/4b90f603738da97723845a56b451f8198618e320.jpg\",\"description\":\"《情人节花束》是一款休闲类网页游戏，游戏大小是0.48M。\"}},{\"score\":0.010947,\"root\":\"植物-花束\",\"keyword\":\"心形礼盒\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E5%BF%83%E5%BD%A2%E7%A4%BC%E7%9B%92\\/6367390\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/838ba61ea8d3fd1ffaf86157324e251f94ca5fee.jpg\",\"description\":\"摇滚明星裘德喜欢收藏稀奇古怪的物品，有十六世纪农夫的骷髅头，有十七世纪女巫的供认状。一天，裘德在网上心血来潮地买了一个鬼魂。不久，他便收到一个黑色的心形盒子，里面放了一件死人穿过的丧服。不久，裘德便慢慢感到了这个鬼魂的存在。网络鬼魂究竟是带着怎样的怨毒而来，它要完成的是一个怎样的复仇计划？本书作者乔·希尔被誉为美国真正的天才作家，曾两次获得恐怖小说奖和20世纪鬼故事奖。\"}}],\"log_id\":5980219783899906350,\"result_num\":5}', '2019-03-14 21:13:07', '1', '杭州市·余杭区');
INSERT INTO `photo_distinguish` VALUES (77, 'oE9el5Hx6LplsHe2roGhrkxkEUsk', '/images/photo/155256921259658749630_src.jpg', '{\"result\":[{\"score\":0.651774,\"root\":\"建筑-现代建筑\",\"keyword\":\"建筑\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E5%BB%BA%E7%AD%91\\/405099\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/0823dd54564e92580e1e71629f82d158ccbf4e10.jpg\",\"description\":\"建筑是建筑物与构筑物的总称，是人们为了满足社会生活需要，利用所掌握的物质技术手段，并运用一定的科学规律、风水理念,美学法则创造的人工环境。\"}},{\"score\":0.500678,\"root\":\"建筑-其他\",\"keyword\":\"居民楼\",\"baike_info\":{}},{\"score\":0.287461,\"root\":\"建筑-其他\",\"keyword\":\"墙\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E5%A2%99\\/84795\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/bf096b63f6246b60170ea4f0e1f81a4c510fa235.jpg\",\"description\":\"墙是一个汉字词语，拼音是qiáng，释义为墙本意是房屋或园场周围的障壁。出处《说文》墙，垣蔽也。会意兼形声。从土或爿，啬声。“啬”有节俭收藏的意思。垒土为墙，意在收藏。\"}},{\"score\":0.147078,\"root\":\"自然风景-天空\",\"keyword\":\"天空\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E5%A4%A9%E7%A9%BA\\/5534\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/b8014a90f603738d6d3004d1b61bb051f919eccf.jpg\",\"description\":\"天空是地球重要的组成部分，在古代中国有天圆地方的说法谓天际空阔。 唐 沈佺期 《绍隆寺》诗：“云盖看木秀，天空见藤盘。” 前蜀 贯休 《送僧归天台寺》诗：“天空闻圣磬，瀑细落花巾。” 2.日月星辰罗列的广大空间。 唐 韩愈 《南山》诗：“天空浮脩眉，浓绿画新就。” 茅盾 《秋收》：“偶尔薄暮时分天空有几片白云，全村的人都欢呼起来。”人可以在天空观测气象或天文现象，从而得知天气变化、时间的流逝或自己的方位。日出日落可知一日中的时间，晚上月亮的盈亏可以知道一个月的时间。北斗星可以指示北方。云的厚度和形状可以知道会否下雨。 在天空可以欣赏到许多美丽的现象，如彩虹、极光和流星雨等。雀鸟会在天空飞翔。 由于石油等化石性燃料使用的增加而产生的悬浮质，特别是那些会在燃烧后释放二氧化硫的煤等燃料的影响，自1973年以来，除了欧洲，天空的能见度正在逐步降低。\"}},{\"score\":0.073539,\"root\":\"建筑-现代建筑\",\"keyword\":\"现代建筑\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E7%8E%B0%E4%BB%A3%E5%BB%BA%E7%AD%91\\/2758782\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/e1bf8725a3023a5f34a80fd7.jpg\",\"description\":\"在一些英文文献中，常用小写字母开头的modern architecture表示广义的现代建筑，以大写字母开头的Modern Architecture或Modernism表示狭义的现代建筑。在本卷中用“现代建筑”表示广义的，而用“现代主义建筑”或“现代派建筑”表示狭义的。在20世纪初期，现代建筑曾经被称为新建筑(newarchitecture)。\"}}],\"log_id\":689885966554850862,\"result_num\":5}', '2019-03-14 21:13:33', '1', '杭州市·滨江区');
