INSERT INTO `photo_distinguish` VALUES (79, 'oE9el5Hx6LplsHe2roGhrkxkEUsk', '/images/photo/155263390580626974071_src.jpg', '{\"result\":[{\"score\":0.99662697315216,\"name\":\"苏铁\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E8%8B%8F%E9%93%81\\/323399\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/32fa828ba61ea8d36fd15f93950a304e241f58b1.jpg\",\"description\":\"苏铁。俗称：铁树，别名：辟火蕉、凤尾蕉、凤尾松、凤尾草，拉丁文名：Cycas revoluta Thunb.苏铁科、苏铁属，一说是因其木质密度大，入水即沉，沉重如铁而得名；另一说因其生长需要大量铁元素，故而名之。又名凤尾蕉、避火蕉、凤尾松，多种植在南方，现广泛分布于中国、日本、菲律宾和印度尼西亚等国家。苏铁最为出名的是其开花，被称之为“铁树开花”。苏铁为优美的观赏树种，栽培极为普遍，茎内含淀粉，可供食用；种子含油和丰富的淀粉，微有毒，供食用和药用，有治痢疾、止咳和止血之效。\"}},{\"score\":0.0090190358459949,\"name\":\"锦毛大苏铁\",\"baike_info\":{}},{\"score\":0.0061578312888741,\"name\":\"剑叶铁树\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E5%89%91%E5%8F%B6%E9%93%81%E6%A0%91\\/6972223\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/77c6a7efce1b9d165afe4e68f5deb48f8c5464fa.jpg\",\"description\":\"剑叶铁树，中药名。为龙舌兰科植物剑叶朱蕉Cordyline stricta Endl.的叶或根茎。分布于广东、广西、云南等地。具有散瘀消肿，凉血止血之功效。常用于跌打损伤，外伤出血，便血，尿血，鼻衄，咳嗽吐血，哮喘，小儿疳积，痢疾。\"}},{\"score\":0.0039891446940601,\"name\":\"蒲葵\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E8%92%B2%E8%91%B5\\/911887\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/d788d43f8794a4c201829db808f41bd5ac6e39fb.jpg\",\"description\":\"蒲葵(拉丁学名：Livistona chinensis (Jacq.) R.Br.)为棕榈科蒲葵属的多年生常绿乔木，高可达20米，基部常膨大，叶阔肾状扇形，果实椭圆形橄榄状。蒲葵不但是一种庭园观赏植物和良好的四旁绿化树种，也是一种经济林树种。可用其嫩叶编制葵扇；老叶制蓑衣等，叶裂片的肋脉可制牙签；果实及根入药。(概述图片来源：中国植物图像库)\"}},{\"score\":0.0025855770800263,\"name\":\"棕榈\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E6%A3%95%E6%A6%88\\/623905\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/0dd7912397dda144235c8d7cb8b7d0a20df486a3.jpg\",\"description\":\"棕榈(学名：Trachycarpus fortunei (Hook.) H.Wendl.)属棕榈科常绿乔木，高可达7米；干圆柱形，叶片近圆形，叶柄两侧具细圆齿，花序粗壮，雌雄异株。花黄绿色，卵球形；果实阔肾形，有脐，成熟时由黄色变为淡蓝色，有白粉，种子胚乳角质。花期4月，果期12月棕榈原产中国。日本、印度、缅甸也有。棕榈是世界上最耐寒的棕榈科植物之一，除西藏外我国黄河以南地区均有分布，北起山东，南到广东、广西和云南、西达西藏边界，东至上海浙江。棕榈树栽于庭院、路边及花坛之中，树势挺拔，叶色葱茏，适于四季观赏。木材可以制器具。棕榈叶鞘为扇子型，有棕纤维，叶可制扇、帽等工艺品，根入药。棕榈科植物以其特有的形态特征构成了热带植物部分特有的景观。\"}}],\"log_id\":845887716813489519}', '2019-03-15 15:11:47', '2', '杭州市·江干区');
