INSERT INTO `photo_distinguish` VALUES (126, 'oE9el5DIylYru220QuAtU5XCVGX4', '/images/photo/155600958957685904197_src.jpg', '{\"result\":[{\"score\":0.17110842466354,\"name\":\"水杉\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E6%B0%B4%E6%9D%89\\/363555\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/a50f4bfbfbedab64ff7d8e65f536afc379311e0c.jpg\",\"description\":\"水杉(学名：Metasequoia glyptostroboides Hu & W.C.Cheng)：裸子植物杉科。落叶乔木，小枝对生，下垂。叶线形，交互对生，假二列成羽状复叶状，长1-1.7厘米，下面两侧有4-8条气孔线。雌雄同株。球果下垂，近球形，微具4棱，长1.8-2.5厘米，有长柄；种鳞木质，盾形，每种鳞具5～9种子，种子扁平，周围具窄翅。水杉属在中生代白垩纪和新生代约有6～7种，过去认为早已绝灭，1941年中国植物学者在湖北利川谋道镇(当时四川万县磨刀溪)首次发现这一闻名中外古老珍稀孑遗树种。据近年调查，重庆万州、重庆石柱县、湖北利川和湖南龙山、桑植均发现300余年的巨树。水杉适应性强，喜湿润生长快，北京以南各地均有栽培。材质轻软，可供建筑、板料、造纸等用；树姿优美，为庭园观赏树。\"}},{\"score\":0.10935696959496,\"name\":\"连香树\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E8%BF%9E%E9%A6%99%E6%A0%91\\/1489072\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/9e3df8dcd100baa1d815ddf34710b912c8fc2eb8.jpg\",\"description\":\"连香树为连香树科连香树属。落叶乔木，高10—20(—40)米，胸径达1米；树皮灰色，纵裂，呈薄片剥落；小枝无毛，有长枝和矩状短枝，短枝在长枝上对生；无顶芽，侧芽卵圆形，芽鳞2。主要生长在温带。该种为第三纪古热带植物的孑遗种单科植物，是较古老原始的木本植物，雌雄异株，结实较少，天然更新困难，资源稀少，已濒临灭绝状态，因此被列入《中国珍稀濒危植物名录》、《中国植物红皮书》和第一批《国家重点保护野生植物名录》，是国家二级重点保护野生植物种。\"}},{\"score\":0.10930202156305,\"name\":\"枫香树\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E6%9E%AB%E9%A6%99%E6%A0%91\\/7068804\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/96dda144ad345982853ee7710cf431adcaef8498.jpg\",\"description\":\"枫香树(Liquidambar formosana Hance)，落叶乔木，高达30米，胸径最大可达1米，树皮灰褐色。喜温暖湿润气候，性喜光，耐干旱瘠薄。产中国秦岭及淮河以南各省，亦见于越南北部，老挝及朝鲜南部。树脂供药用，能解毒止痛，止血生肌；根、叶及果实亦入药，有祛风除湿，通络活血功效。木材稍坚硬，可制家具及贵重商品的装箱。\"}},{\"score\":0.077297791838646,\"name\":\"光叶珙桐\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E5%85%89%E5%8F%B6%E7%8F%99%E6%A1%90\\/7153719\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/4afbfbedab64034f88eaf2ecafc379310a551d30.jpg\",\"description\":\"光叶珙桐是珙桐的一个变种。别名：水梨子、鸽子树、鸽子花树，是双子叶植物，光叶珙桐属于蓝果树科、珙桐属 (Davidia)。落叶乔木；叶互生，单叶，全缘或有齿缺，无托叶；花两性或单性，排成顶生或腋生的头状花序或为伞房状或伞形状的聚伞花序，萼小。两性花位于花序的顶端，雄花环绕于其周围，雄花无花萼及花瓣，紫色。果实为长卵圆形核果，紫绿色具黄色斑点。产湖北西部、四川、贵州等省；常与珙桐混生。产湖北西部、四川、贵州等省；常与珙桐混生。具有重要的科研，经济及观赏价值。\"}},{\"score\":0.063166864216328,\"name\":\"鸡爪槭\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E9%B8%A1%E7%88%AA%E6%A7%AD\\/805917\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/060828381f30e924b8a5b96047086e061d95f759.jpg\",\"description\":\"鸡爪槭(学名：Acer palmatum Thunb.)是槭树科，槭属落叶小乔木；树冠伞形。树皮平滑。树皮深灰色。小枝紫或淡紫绿色,老枝淡灰紫色。叶近圆形，基部心形或近心形，掌状，常7深裂，密生尖锯齿。后叶开花；花紫色，杂性，雄花与两性花同株；伞房花序。萼片卵状披针形；花瓣椭圆形或倒卵形。幼果紫红色，熟后褐黄色，果核球形，脉纹显著，两翅成钝角。花果期5～9月。分布于中国华东、华中至西南等省区。生于低海拔的林边或疏林中。朝鲜和日本也有分布。各国早已引种栽培，其中有红槭,羽毛槭常作园林树种。鸡爪槭喜欢阳光，忌西射，西射会焦叶。较耐阴，在高大树木庇荫下长势良好。对二氧化硫和烟尘抗性较强。其叶形美观，入秋后转为鲜红色，色艳如花，灿烂如霞，为优良的观叶树种。(概述图参考来源：中国自然标本馆)\"}}],\"log_id\":5676196135214393815}', '2019-04-23 16:53:12', '2', '杭州市·西湖区');
INSERT INTO `photo_distinguish` VALUES (127, 'oE9el5Hx6LplsHe2roGhrkxkEUsk', '/images/photo/155602752105687985778_src.jpg', '{\"result\":[{\"score\":0.82676148414612,\"name\":\"红枫\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E7%BA%A2%E6%9E%AB\\/1336\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/83025aafa40f4bfb3c188a19094f78f0f63618b6.jpg\",\"description\":\"红枫，拉丁学名为：Acer palmatum \'Atropurpureum\'，又名红颜枫，为槭树科鸡爪槭的变型，是落叶乔木，产于浙江，安徽，江苏，河南，江西，上海等地。红枫树高2—8米，枝条多细长光滑，偏紫红色。叶掌状，5-7深裂纹，直径5—10CM，裂片卵状披针形，先端尾状尖，缘有重锯齿。花顶生伞房花序，紫色。翅果，翅长2-3CM，两翅间成钝角。红枫性喜阳光，适合温暖湿润气候，怕烈日曝晒，较耐寒，稍耐旱，不耐涝，适生于肥沃疏松排水良好的土壤。早春发芽时，嫩叶艳红，密生白色软毛，叶片舒展后渐脱落，叶色亦由艳丽转淡紫色甚至泛暗绿色。红枫为名贵的观叶树木，故常作盆栽欣赏。枫是落叶乔木或灌木。叶对生。如梣叶枫(A.negundo)的叶是羽状的。枫树中有雌雄同株，也有雌雄异株的种。有的种为虫媒，有的则是风媒。花有五瓣，雄蕊部分退化。子房上位。\"}},{\"score\":0.086411394178867,\"name\":\"凤凰木\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E5%87%A4%E5%87%B0%E6%9C%A8\\/38426\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/18d8bc3eb13533fa1327bd30abd3fd1f41345b1e.jpg\",\"description\":\"凤凰木(Delonix regia)，取名于“叶如飞凰之羽，花若丹凤之冠”，别名金凤花、红花楹树、火树、洋楹等。豆科，落叶乔木，高可达20米。树冠宽广。二回羽状复叶，小叶长椭圆形。夏季开花，总状花序，花大，红色，有光泽。荚果木质，长可达50厘米。凤凰木因鲜红或橙色的花朵配合鲜绿色的羽状复叶，被誉为世上最色彩鲜艳的树木之一。凤凰木是非洲马达加斯加共和国的国树，也是厦门市、台湾台南市、四川攀枝花市的市树，广东省汕头市的市花，民国时期广东湛江市的市花，汕头大学、厦门大学的校花。凤凰木植株高大，由于树冠横展而下垂，浓密阔大而招风，在热带地区担任遮荫树的角色。性喜高温、多日的环境，须在阳光充足处方能繁茂生长。分布于中国南部及西南部、原产地马达加斯加及世界各热带地方。\"}},{\"score\":0.067685179412365,\"name\":\"红叶\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E7%BA%A2%E5%8F%B6\\/61924\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/b151f8198618367a6b04186324738bd4b21ce5fb.jpg\",\"description\":\"红叶，是一类观赏树木，主要看叶，为历代文人青睐，最早见于司马相如《上林赋》。红叶，全国各地都有，千佛山亦鼎鼎大名，然而尤以山南山北最盛。山北瀛芳园处，星罗棋布，万紫千红。卵形的叶，杂性的花，浓郁的香，甚是诱人，再加上丹枫、火炬、红叶李、红叶树，将其衬托得更加艳丽。山南和山北相反，这里很是单纯，面积也大，号称百亩，覆盖了整个山坡。若漫步在通幽曲径上，便会看到棵棵黄栌红衣少女似的夹道迎宾。两边 树枝搭连，又似红色长廊。放眼山坡，一簇簇，一片片，加翠柏缀其间，颇显风韵，若是清晨，又遇朝阳甘露，更像红色绸缎上撒满了颗颗红色珍珠，闪烁着华美的光芒。\"}},{\"score\":0.051186218857765,\"name\":\"鸡爪槭\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E9%B8%A1%E7%88%AA%E6%A7%AD\\/805917\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/060828381f30e924b8a5b96047086e061d95f759.jpg\",\"description\":\"鸡爪槭(学名：Acer palmatum Thunb.)是槭树科，槭属落叶小乔木；树冠伞形。树皮平滑。树皮深灰色。小枝紫或淡紫绿色,老枝淡灰紫色。叶近圆形，基部心形或近心形，掌状，常7深裂，密生尖锯齿。后叶开花；花紫色，杂性，雄花与两性花同株；伞房花序。萼片卵状披针形；花瓣椭圆形或倒卵形。幼果紫红色，熟后褐黄色，果核球形，脉纹显著，两翅成钝角。花果期5～9月。分布于中国华东、华中至西南等省区。生于低海拔的林边或疏林中。朝鲜和日本也有分布。各国早已引种栽培，其中有红槭,羽毛槭常作园林树种。鸡爪槭喜欢阳光，忌西射，西射会焦叶。较耐阴，在高大树木庇荫下长势良好。对二氧化硫和烟尘抗性较强。其叶形美观，入秋后转为鲜红色，色艳如花，灿烂如霞，为优良的观叶树种。(概述图参考来源：中国自然标本馆)\"}},{\"score\":0.05010087043047,\"name\":\"枫香树\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E6%9E%AB%E9%A6%99%E6%A0%91\\/7068804\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/96dda144ad345982853ee7710cf431adcaef8498.jpg\",\"description\":\"枫香树(Liquidambar formosana Hance)，落叶乔木，高达30米，胸径最大可达1米，树皮灰褐色。喜温暖湿润气候，性喜光，耐干旱瘠薄。产中国秦岭及淮河以南各省，亦见于越南北部，老挝及朝鲜南部。树脂供药用，能解毒止痛，止血生肌；根、叶及果实亦入药，有祛风除湿，通络活血功效。木材稍坚硬，可制家具及贵重商品的装箱。\"}}],\"log_id\":636756504365878007}', '2019-04-23 21:52:02', '2', '杭州市·西湖区');
INSERT INTO `photo_distinguish` VALUES (128, 'oE9el5DIylYru220QuAtU5XCVGX4', '/images/photo/155609490261233390650_src.jpg', '{\"result\":[{\"score\":0.116482,\"root\":\"商品-家具\",\"keyword\":\"床\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E5%BA%8A\\/13034502\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/e850352ac65c10386752dd9cb5119313b07e89bb.jpg\",\"description\":\"床一般在卧室、宿舍、病房、旅馆等场所使用，指供睡觉用的支具。以木材、不锈钢等金属为材料，以床头床尾、床腿、床板床垫、左右折叠护挡、静音脚轮、一体餐桌、置物架为组件，采用冲孔、装配、焊接、压铆、除锈、喷塑等工艺。通常用以满足人类日常睡觉起居的器具，具有阻燃、防鼠、防蛀、耐用、简约美观、易清洁、移动方便等特点。人的三分之一的时间都是在床上度过的。 经过千百年的不断演化和改进设计，在满足常人睡觉的基础上，装上配套床垫、气垫、医疗康复设备、尿袋挂钩，也增加了一些就餐、电动起背、定时翻身、移动行走、站立、接尿、坐躺、取暖降温等其它功能，即可满足常人睡觉，也可满足儿童、重度病人、残障人士、老年人、医院病人、在家医疗康复、学习生活、办公应急等各种需要。床的种类有平板床、单人床、双人床、多功能关节康复器床、电动起背床、定时翻身床、残障床、四柱床、双层床、儿童床、病号床、医疗床、折叠床、护理床、按摩床、电动站床、气垫床、日床等。\"}},{\"score\":0.087823,\"root\":\"人物-人物特写\",\"keyword\":\"美女\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E7%BE%8E%E5%A5%B3\\/109596\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/91529822720e0cf3acdfc0280046f21fbe09aa3d.jpg\",\"description\":\"美女是一个汉语词汇，拼音是měi nǚ，指容貌姣好、仪态优雅的女子。中国古代关于美女的形容词和诗词歌赋众多，形成了丰富的美学资料。《墨子·公孟》：“譬若美女，处而不出，人争求之。”\"}},{\"score\":0.059325,\"root\":\"商品-家具\",\"keyword\":\"柜子\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E6%9F%9C%E5%AD%90\\/109413\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/f9dcd100baa1cd11cafe4c3dbb12c8fcc3ce2d66.jpg\",\"description\":\"拼音：guì zǐ，英文名：cabinet。释义：收藏衣物、文件等用的器具，方形或长方形，一般为木制或铁制。柜子是一种长方形家具，形体较高大，可存放物品。柜子的使用始于夏商周时期，那时称为“椟”。到了明清时期，柜子成为室内必备的家具，且形制已定型。明清柜子按形制可分为方角柜、圆角柜、亮格柜，形制不同，其构成部件也有不同。柜类家具主要是指以木材、人造板或金属等材料制成的各种用途不同的柜子。\"}},{\"score\":0.030655,\"root\":\"商品-军火\",\"keyword\":\"弩\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E5%BC%A9\\/2223715\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/bbe0d3117dade52eb9127bc1.jpg\",\"description\":\"弩是古代的一种冷兵器，是古代兵车战法中的重要组成部分。是步兵有效克制骑兵的一种武器。 弩也被称作“窝弓”、“十字弓”。古代用来射箭的一种兵器。它是一种装有臂的弓，主要由弩臂、弩弓、弓弦和弩机等部分组成。虽然弩的装填时间比弓长很多，但是它比弓的射程更远，杀伤力更强，命中率更高，对使用者的要求也比较低，是古代一种大威力的远距离杀伤武器。强弩的射程可达600米，特大型床弩的射程可达千米。按张弦的方法不同，可分为臂张弩、踏张弩和腰张弩等，还有能数箭齐射或连射的连弩和装有数把弩弓的床弩。\"}},{\"score\":0.002417,\"root\":\"商品-家具\",\"keyword\":\"椅子\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E6%A4%85%E5%AD%90\\/318232\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/342ac65c103853432384366e9513b07eca808832.jpg\",\"description\":\"椅子是一种日常生活家具，一种有靠背、还有扶手的坐具。古代席地而坐，原没有椅子。据文籍记载，椅子的名称始见于唐代，而椅子的形象则要上溯到汉魏时传入北方的胡床。\"}}],\"log_id\":6545277197280596760,\"result_num\":5}', '2019-04-24 16:35:03', '1', '杭州市·西湖区');
INSERT INTO `photo_distinguish` VALUES (129, 'oE9el5DIylYru220QuAtU5XCVGX4', '/images/photo/155609492913281165311_src.jpg', '{\"result\":[{\"score\":0.925349,\"root\":\"非自然图像-屏幕截图\",\"keyword\":\"屏幕截图\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E5%B1%8F%E5%B9%95%E6%88%AA%E5%9B%BE\\/3634161\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/aa18972bd40735fa016882639c510fb30e240854.jpg\",\"description\":\"所谓屏幕截图就是将电脑屏幕上的桌面、窗口、对话框、选项卡等屏幕元素保存为图片。在Windows下用户可以使用键盘上的“打印屏幕系统请求”(Print Screen)按键进行整个屏幕的截图和当前活动窗口的截图(按住Alt键的同时按下Print Screen键)，还可以借助专业的屏幕截图软件进行截图。\"}},{\"score\":0.574478,\"root\":\"非自然图像-图像素材\",\"keyword\":\"输入法皮肤\",\"baike_info\":{}},{\"score\":0.386188,\"root\":\"非自然图像-海报\",\"keyword\":\"绿灯侠\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E7%BB%BF%E7%81%AF%E4%BE%A0\\/62325\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/dc54564e9258d109229f8eebd358ccbf6c814d6e.jpg\",\"description\":\"影片改编自DC漫画公司漫画，由马丁·坎贝尔执导，迈克尔·戈登伯格和迈克尔·格林担任编剧，瑞安·雷诺兹、布蕾克·莱弗利、彼得·萨斯加德,马克·斯特朗联袂出演，影片于2011年6月17日在美国上映。影片讲述宇宙间有一群维护宇宙治安的“绿灯军团”，每个成员都配备了一枚神奇的戒指，可以随心所欲的变出各种东西或实现任何目的。哈尔·乔丹原本是一名试飞员，后来被绿灯侠的戒指召唤成为“绿灯侠”，负责保卫地球和银河地区的治安的故事。\"}},{\"score\":0.191715,\"root\":\"商品-电脑办公\",\"keyword\":\"显示器屏幕\",\"baike_info\":{}},{\"score\":0.003704,\"root\":\"非自然图像-图像素材\",\"keyword\":\"小窝\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E5%B0%8F%E7%AA%9D\\/10959297\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/0df3d7ca7bcb0a46c923b6fd6b63f6246a60aff4.jpg\",\"description\":\"小窝 foveola [xiǎo wō]指上皮的小凹陷。由与覆盖在管状和囊状器官内腔的表层上皮细胞同一类的细胞组成，所以，即使有的具分泌机能，但也多可与腺体相区别。\"}}],\"log_id\":1010975983996273240,\"result_num\":5}', '2019-04-24 16:35:30', '1', '杭州市·西湖区');
INSERT INTO `photo_distinguish` VALUES (132, 'oE9el5Hx6LplsHe2roGhrkxkEUsk', '/images/photo/155619431342938355551_src.jpg', '{\"result\":[{\"score\":\"0.349521\",\"name\":\"短毛家猫\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E7%9F%AD%E6%AF%9B%E5%AE%B6%E7%8C%AB\\/1061735\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/f9198618367adab4137e9c1388d4b31c8701e430.jpg\",\"description\":\"短毛家猫其实是混种家猫的好听叫法，常被称为普通猫或里巷猫(alley cat)。优良的展出动物应为纯系，血统可考，按习惯的外观标准精心育成。\"}},{\"score\":\"0.190522\",\"name\":\"英国短毛猫\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E8%8B%B1%E5%9B%BD%E7%9F%AD%E6%AF%9B%E7%8C%AB\\/672846\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/37d12f2eb9389b5039d4d0c88c35e5dde7116e04.jpg\",\"description\":\"英国短毛猫，体形圆胖，四肢粗短发达，毛短而密，头大脸圆，温柔平静，对人友善，极易饲养。大而圆的眼睛根据被毛不同而呈现各种颜色。作为一个古老的猫品种，其历史可追溯至古罗马时期的家猫，由于拥有悠久的育种历史，可以称得上是猫家族中的典范。英国短毛猫除了拥有固定而聚，代表性的遗传特征之外，又具有丰富的变异性，如背毛色眼睛颜色等。更重要的是，有了广泛的配种历史后，这种猫拥有了更健康的身体和更温驯的性格。\"}},{\"score\":\"0.139552\",\"name\":\"美国短毛猫\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E7%BE%8E%E5%9B%BD%E7%9F%AD%E6%AF%9B%E7%8C%AB\\/300902\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/80cb39dbb6fd5266b5c16430a118972bd407362f.jpg\",\"description\":\"美国短毛猫，又称美洲短毛虎纹猫，是美国人把欧洲猫与美洲大陆的土种猫加以改良而育成，是家猫中的传统品种。1971年，其被选为美国最好的猫种之一。\"}},{\"score\":\"0.0300141\",\"name\":\"异国短毛猫\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E5%BC%82%E5%9B%BD%E7%9F%AD%E6%AF%9B%E7%8C%AB\\/4630871\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/e61190ef76c6a7efac2ebfa1f5faaf51f3de6628.jpg\",\"description\":\"1960年左右，美国的育种专家将美国短毛猫,波斯猫进行交配繁育，期待改进美国猫的皮毛颜色并增加其体重，这样就诞生了异国短毛猫。在1966年被 CFA 承认为新品种。在育种期间，它还和俄罗斯蓝猫及缅甸猫杂交。1987 年以来，该品种的允许杂交品种被限定为波斯猫一种。FIFE 在 1986 年承认了异国短毛猫。该品种在美国已经非常普遍，在欧洲也在逐渐风行起来。异国短毛猫除拥有浓密皮毛外，还保留了波斯猫独特的可爱表情与圆滚滚的体型。性格也如波斯猫般文静、亲切，能慰藉主人的心，体形为中型到大型的短脚型，头部宽而圆，鼻子有明显的凹陷，皮毛有柔和的光泽，性情独立，不爱吵闹。\"}},{\"score\":\"0.0217119\",\"name\":\"美国短尾猫\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E7%BE%8E%E5%9B%BD%E7%9F%AD%E5%B0%BE%E7%8C%AB\\/645762\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/63d9f2d3572c11df296828c5652762d0f603c29f.jpg\",\"description\":\"美国短尾猫(学名：Lynx rufus)短尾猫，体长，0.7-1.2米，身高，肩高36-38厘米。是分布在北美洲的一种猫科动物。短尾猫是有很强适应力的掠食者，栖息在林地、半沙漠、城市及沼泽。短尾猫是拂晓的动物，主要于黎明及黄昏时出没，以兔、野兔、昆虫、小型啮齿动物为食。野外短尾猫的最长寿有16岁。\"}},{\"score\":\"0.019646\",\"name\":\"苏格兰折耳猫\"}],\"log_id\":6071589347315971641}', '2019-04-25 20:11:54', '3', '杭州市·西湖区');
INSERT INTO `photo_distinguish` VALUES (133, 'oE9el5Hx6LplsHe2roGhrkxkEUsk', '/images/photo/155619435801557962809_src.jpg', '{\"result\":[{\"score\":0.87955498695374,\"name\":\"红花酢浆草\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E7%BA%A2%E8%8A%B1%E9%85%A2%E6%B5%86%E8%8D%89\\/857480\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/b3119313b07eca80e2b0df5f9c2397dda04483d5.jpg\",\"description\":\"红花酢浆草(学名：Oxalis corymbosa DC.)是酢浆草科，酢浆草属多年生直立草本植物。无地上茎，地下球状鳞茎，鳞片膜质，褐色，叶基生；叶柄被毛；小叶片扁圆状倒心形，顶端凹入，两侧角圆形，背面浅绿色，托叶长圆形，顶部狭尖。总花梗基生，二歧聚伞花序，花梗、苞片、萼片均被毛；萼片披针形，花瓣倒心形，淡紫色至紫红色，花丝被长柔毛；花柱被锈色长柔毛，3-12月开花结果。原产南美热带地区，分布于中国河北、陕西、华东、华中、华南、四川和云南等地。生于低海拔的山地、路旁、荒地或水田中。红花酢浆草小花繁多，烂漫可爱。可布置成花坛、花境、花丛、花群及花台等。该种全草入药，治跌打损伤、赤白痢，止血。(概述图参考来源：)\"}},{\"score\":0.85411834716797,\"name\":\"风铃草\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E9%A3%8E%E9%93%83%E8%8D%89\\/680375\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/b17eca8065380cd79a16c328ab44ad34588281ee.jpg\",\"description\":\"风铃草(拉丁名：Campanula L.)为桔梗科的一年生、二年生或多年生草本。花钟状，通常蓝色，喜夏季凉爽、冬季温和的气候，喜光照充足环境，可耐半阴。风铃草全属二百多种，几乎全在北温带，多数种类产于欧亚大陆北部，少数在北美。我国近20种，主产西南山区，少数种类产北方，个别种也产广东、广西和湖北西部。\"}},{\"score\":0.054662019014359,\"name\":\"酢浆草\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E9%85%A2%E6%B5%86%E8%8D%89\\/4507113\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/4e4a20a4462309f72595e5b4780e0cf3d6cad69a.jpg\",\"description\":\"酢浆草(学名：Oxalis corniculata L.)：多年生草本植物，全体有疏柔毛；茎匍匐或斜升，多分枝。叶互生，掌状复叶有3小叶，倒心形，小叶无柄。花黄色，喜向阳、温暖、湿润的环境，夏季炎热地区宜遮半荫，抗旱能力较强，不耐寒，一般园土均可生长，但以腐殖质丰富的砂质壤土生长旺盛，夏季有短期的休眠。全草入药，有清热解毒，消肿散疾的效用。\"}},{\"score\":0.029922232031822,\"name\":\"紫叶酢浆草\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E7%B4%AB%E5%8F%B6%E9%85%A2%E6%B5%86%E8%8D%89\\/4171946\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/77094b36acaf2edd8a1630c18f1001e939019336.jpg\",\"description\":\"紫叶酢浆草(学名：Oxalis triangularis subsp.papilionacea (Hoffmanns.ex Zucc.) Lourteig)是酢浆草科，酢浆草属多年生草本植物，具球根的草本植物。株高可达30厘米，鳞茎会不断增生。叶丛生于基部，全部为根生叶。掌状复叶。叶片颜色为艳丽的紫红色，部分品种的叶片内侧还镶嵌有如蝴蝶般的紫黑色斑块。伞形花序，花冠淡紫色或白色，端部呈淡粉色。如遇阴雨天，粉红带浅白色的小花只含花苞但不会开放。花期从5月开始，长达数月。原分布南美巴西，中国已成功引种。紫叶酢浆草喜欢温暖湿润的环境，比较能耐干旱，平时都可以给予全日照来养护。紫叶酢浆草可为庭院、阳台、居家增添几分景观。(概述图参考来源：中国自然标本馆)\"}},{\"score\":0.008820278570056,\"name\":\"关节酢浆草\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E9%85%A2%E6%B5%86%E8%8D%89%E5%B1%9E\\/3749158\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/3790312ef763bb6f4fc226bf.jpg\",\"description\":\"酢浆草属(Oxalis L.)是酢浆草科下最大的属，545种左右。除了在极地外，酢浆草属在世界各地均有分布，在巴西、墨西哥的热带及南非物种尤其丰富。\"}}],\"log_id\":3893301630809839993}', '2019-04-25 20:12:38', '2', '杭州市·西湖区');
INSERT INTO `photo_distinguish` VALUES (134, 'oE9el5Hx6LplsHe2roGhrkxkEUsk', '/images/photo/155643109147858953045_src.jpg', '{\"result\":[{\"score\":0.782631,\"root\":\"自然风景-天空\",\"keyword\":\"天空\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E5%A4%A9%E7%A9%BA\\/5534\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/241f95cad1c8a7868fa019076909c93d70cf5034.jpg\",\"description\":\"天空是地球重要的组成部分，在古代中国有天圆地方的说法谓天际空阔。 唐 沈佺期 《绍隆寺》诗：“云盖看木秀，天空见藤盘。” 前蜀 贯休 《送僧归天台寺》诗：“天空闻圣磬，瀑细落花巾。” 2.日月星辰罗列的广大空间。 唐 韩愈 《南山》诗：“天空浮脩眉，浓绿画新就。” 茅盾 《秋收》：“偶尔薄暮时分天空有几片白云，全村的人都欢呼起来。”人可以在天空观测气象或天文现象，从而得知天气变化、时间的流逝或自己的方位。日出日落可知一日中的时间，晚上月亮的盈亏可以知道一个月的时间。北斗星可以指示北方。云的厚度和形状可以知道会否下雨。 在天空可以欣赏到许多美丽的现象，如彩虹、极光和流星雨等。雀鸟会在天空飞翔。 由于石油等化石性燃料使用的增加而产生的悬浮质，特别是那些会在燃烧后释放二氧化硫的煤等燃料的影响，自1973年以来，除了欧洲，天空的能见度正在逐步降低。\"}},{\"score\":0.614415,\"root\":\"自然风景-湖泊\",\"keyword\":\"湖泊\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E6%B9%96%E6%B3%8A\\/242936\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/8435e5dde71190ef635cd56ccd1b9d16fcfa60d9.jpg\",\"description\":\"湖盆及其承纳的水体。湖盆是地表相对封闭可蓄水的天然洼池。湖泊按成因可分为构造湖、火山口湖、冰川湖、堰塞湖、喀斯特湖、河成湖、风成湖、海成湖和人工湖(水库)等。按泄水情况可分为外流湖(吞吐湖)和内陆湖；按湖水含盐度可分为淡水湖(含盐度小于1g\\/L)、咸水湖(含盐度为1-35g\\/L)和盐湖(含盐度大于35g\\/L)。湖水的来源是降水、地面径流、地下水，有的则来自冰雪融水。湖水的消耗主要是蒸发、渗漏、排泄和开发利用。\"}},{\"score\":0.439106,\"root\":\"自然图像-气候气象\",\"keyword\":\"霞光\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E9%9C%9E%E5%85%89\\/5109934\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/8ad4b31c8701a18bfc80a8a6942f07082938fef4.jpg\",\"description\":\"《霞光》是3D动画《精灵世纪》的片尾曲，由陈欣若作曲，高博作词，陈欣若、闫志强编曲，演唱者是曲锦楠、白冰，发行于2006年7月28日。\"}},{\"score\":0.262889,\"root\":\"自然图像-火灾烟雾\",\"keyword\":\"熊熊大火\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E7%86%8A%E7%86%8A%E5%A4%A7%E7%81%AB\\/19257950\",\"description\":\"常用成语， 形容火势旺盛而猛烈 。\"}},{\"score\":0.079551,\"root\":\"商品-公共设施\",\"keyword\":\"街灯\",\"baike_info\":{}}],\"log_id\":831929795