INSERT INTO `place` VALUES (1013, '午潮山国家森林公园金莲寺', '浙江省杭州市西湖区转塘街道午潮山国家森林公园', '(0571)86902690', '58fb6d6eb2fd1b45c2f1cf0d', '寺庙', '景区大;1432人访问', 'http://hiphotos.baidu.com/lbsugc/pic/item/1b4c510fd9f9d72a3094d7afd92a2834359bbb94.jpg', NULL, NULL, '10:00-22:00', '[]', '[{\"overall_rating\":5,\"date\":\"2018-06-29 08:31\",\"one_url\":null,\"service_rating\":null,\"user_url\":null,\"user_url_mobile\":null,\"poi_id\":\"18012152588622926378\",\"user_name\":\"starinfine\",\"uniqkey\":\"6af9bad3502d5ce32fec61445a63eafc\",\"user_logo\":null,\"time_stamp\":1530232274,\"one_url_mobile\":null,\"price\":0,\"content\":\"金莲寺内名人题额、题联甚多，寺前横匾“金莲寺”，据说是彭公所书。1958年，金莲寺被拆除，旧寺地基尚存，但仍随处可见断墙残壁、古陶瓷与砖瓦碎片。八十年代，龙门坎村民间信徒集资在原基地上建一小庙，这就是现在的金莲寺，但规模、装饰考究程度，却远远不能与昔日的金莲寺相提并论。\",\"cmt_icons\":{\"comment_icon\":[]},\"video_url\":null,\"video_pic\":null,\"video_time\":null,\"media\":0,\"comment_num\":4,\"src\":\"baidumap\",\"former_source\":\"ugc\",\"cn_name\":\"百度地图\",\"from\":\"map.baidu.com\",\"comment_url\":null,\"comment_url_mobile\":null,\"uid\":\"18012152588622926378:ugc\",\"mark_source\":10,\"mark_duration\":10,\"mark_multimedia\":0,\"mark_content\":35,\"mark_userscore\":10,\"mark_total\":65,\"cmt_id\":\"311366921\",\"isAgree\":null,\"favorNum\":0,\"reply\":null},{\"overall_rating\":5,\"date\":\"2018-10-21 14:56\",\"one_url\":null,\"service_rating\":null,\"user_url\":null,\"user_url_mobile\":null,\"cmt_icons\":{\"comment_icon\":[]},\"user_name\":\"太申\",\"uniqkey\":\"eb8448958da59b14123be2f32fe4652e\",\"user_logo\":null,\"time_stamp\":1540105005,\"one_url_mobile\":null,\"price\":0,\"content\":\"\",\"pics\":[{\"pic_name\":\"\\\"null\\\"\",\"pic_url\":\"http://g.hiphotos.baidu.com/lbsugc/pic/item/730e0cf3d7ca7bcb2c9bfd4cb3096b63f724a8f4.jpg\",\"pic_type\":\"0\",\"pic_pv\":208}],\"video_url\":null,\"video_pic\":null,\"video_time\":null,\"media\":1,\"poi_id\":\"18012152588622926378\",\"src\":\"baidumap\",\"former_source\":\"ugc\",\"cn_name\":\"百度地图\",\"from\":\"map.baidu.com\",\"comment_url\":null,\"com