INSERT INTO `place_comment` VALUES (2277, '8a098874a8567389b34c4914', '西湖夜景也是美如画，六公园附近很热闹，在湖面上有一座九曲桥，贴着湖面建造，湖水平静，走在上面有水上漂的感觉，有时候，雨过天晴，会涨高水面，把桥面淹没，穿雨鞋或凉鞋在上面走也是一种乐趣<br>夜晚能见度很高的时候，对面的宝石山也有亮灯工程，山上的保俶塔清晰可见，非常漂亮，像极了一副油画，人在景中游，恍惚穿越了时空，来到古代<br>一边是现代的大华饭店，房屋建筑也是年代历史的见证，灯光照影下很富丽堂皇，忍不住就拿起相机记录下来<br>美丽的西湖，美丽的六公园', '[{\"pic_url\":\"http://qcloud.dpfile.com/pc/nRKezUsP4GtCEEfj2mNAUwIlxMR2pEgChmsZvunxeGT9FiMxfamhzOpBpUB36ZKEjoJrvItByyS4HHaWdXyO_DrXIaWutJls2xCVbatkhjUNNiIYVnHvzugZCuBITtvjski7YaLlHpkrQUr5euoQrg.jpg\",\"big_photo\":\"http://qcloud.dpfile.com/pc/nRKezUsP4GtCEEfj2mNAUwIlxMR2pEgChmsZvunxeGT9FiMxfamhzOpBpUB36ZKEjoJrvItByyS4HHaWdXyO_DrXIaWutJls2xCVbatkhjUNNiIYVnHvzugZCuBITtvjski7YaLlHpkrQUr5euoQrg.jpg\",\"pic_pv\":13},{\"pic_url\":\"http://qcloud.dpfile.com/pc/_FPFdrPEHU06WhyBHaCO2f1EukYI0qKI1PGmp06ptERm2cIggjFEH_RcDG1HWAsVjoJrvItByyS4HHaWdXyO_DrXIaWutJls2xCVbatkhjUNNiIYVnHvzugZCuBITtvjski7YaLlHpkrQUr5euoQrg.jpg\",\"big_photo\":\"http://qcloud.dpfile.com/pc/_FPFdrPEHU06WhyBHaCO2f1EukYI0qKI1PGmp06ptERm2cIggjFEH_RcDG1HWAsVjoJrvItByyS4HHaWdXyO_DrXIaWutJls2xCVbatkhjUNNiIYVnHvzugZCuBITtvjski7YaLlHpkrQUr5euoQrg.jpg\",\"pic_pv\":0},{\"pic_url\":\"http://qcloud.dpfile.com/pc/dTH1jWmFTOYb0MU--2ioniHAeTdZKPDqPcT3BpNin3QCCnq-2fPZxM8ThQ2sImF6joJrvItByyS4HHaWdXyO_DrXIaWutJls2xCVbatkhjUNNiIYVnHvzugZCuBITtvjski7YaLlHpkrQUr5euoQrg.jpg\",\"big_photo\":\"http://qcloud.dpfile.com/pc/dTH1jWmFTOYb0MU--2ioniHAeTdZKPDqPcT3BpNin3QCCnq-2fPZxM8ThQ2sImF6joJrvItByyS4HHaWdXyO_DrXIaWutJls2xCVbatkhjUNNiIYVnHvzugZCuBITtvjski7YaLlHpkrQUr5euoQrg.jpg\",\"pic_pv\":1}]', 0, '2018-07-16 00:00', NULL, '齐齐_6087', 'https://p1.meituan.net/userheadpicbackend/9db4778b0bef4f4d5103e383a4309d7352618.jpg%40120w_120h_1e_1c_1l%7Cwatermark%3D0', 5.00, 1, '大众点评');
