INSERT INTO `photo_distinguish` VALUES (20, 'oE9el5Hx6LplsHe2roGhrkxkEUsk', '/images/photo/154814007708176151683_src.jpg', '{\"result\":[{\"score\":0.973327,\"root\":\"公众人物\",\"keyword\":\"周杰伦\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E5%91%A8%E6%9D%B0%E4%BC%A6\\/129156\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/38dbb6fd5266d0167927ca029b2bd40735fa35d9.jpg\",\"description\":\"周杰伦(Jay Chou)，1979年1月18日出生于台湾省新北市，中国台湾流行乐男歌手、音乐人、演员、导演、编剧、监制、商人。2000年发行首张个人专辑《Jay》。2001年发行的专辑《范特西》奠定其融合中西方音乐的风格。2002年举行“The One”世界巡回演唱会。2003年成为美国《时代周刊》封面人物。2004年获得世界音乐大奖中国区最畅销艺人奖。2005年凭借动作片《头文字D》获得台湾电影金马奖、香港电影金像奖最佳新人奖。2006年起连续三年获得世界音乐大奖中国区最畅销艺人奖。2007年自编自导的文艺片《不能说的秘密》获得台湾电影金马奖年度台湾杰出电影奖。2008年凭借歌曲《青花瓷》获得第19届金曲奖最佳作曲人奖。2009年入选美国CNN评出的“25位亚洲最具影响力的人物”；同年凭借专辑《魔杰座》获得第20届金曲奖最佳国语男歌手奖。2010年入选美国《Fast Company》评出的“全球百大创意人物”。2011年凭借专辑《跨时代》再度获得金曲奖最佳国语男歌手奖，并且第4次获得金曲奖最佳国语专辑奖；同年主演好莱坞电影《青蜂侠》。2012年登福布斯中国名人榜榜首。2014年发行华语乐坛首张数字音乐专辑《哎呦，不错哦》。2018年举行“地表最强2世界巡回演唱会”。演艺事业外，他还涉足商业、设计等领域。2007年成立杰威尔有限公司。2011年担任华硕笔电设计师并入股香港文化传信集团。周杰伦热心公益慈善，多次向中国内地灾区捐款捐物。2008年捐款援建希望小学。2014年担任中国禁毒宣传形象大使。\"}},{\"score\":0.657525,\"root\":\"人物-人物特写\",\"keyword\":\"鬓发\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E9%AC%93%E5%8F%91\\/4057208\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/7af40ad162d9f2d38cab570bacec8a136227cc98.jpg\",\"description\":\"鬓发，是在耳朵前面的一绺头发或一簇卷发。也指男士上承头发，下接胡须的中场地带，作用不可小觑，不仅是男发造型中最考功力的地方，更是修饰脸型、决定气质的关键。\"}},{\"score\":0.408127,\"root\":\"人物-人物特写\",\"keyword\":\"人脸\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E4%BA%BA%E8%84%B8\\/9106961\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/4d086e061d950a7b1d387baf08d162d9f2d3c94f.jpg\",\"description\":\"《人脸》是贯犯四分之一写的网络小说连载于起点中文网。\"}},{\"score\":0.207997,\"root\":\"非自然图像-屏幕截图\",\"keyword\":\"屏幕截图\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E5%B1%8F%E5%B9%95%E6%88%AA%E5%9B%BE\\/3634161\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/aa18972bd40735fa016882639c510fb30e240854.jpg\",\"description\":\"所谓屏幕截图就是将电脑屏幕上的桌面、窗口、对话框、选项卡等屏幕元素保存为图片。在Windows下用户可以使用键盘上的“打印屏幕系统请求”(Print Screen)按键进行整个屏幕的截图和当前活动窗口的截图(按住Alt键的同时按下Print Screen键)，还可以借助专业的屏幕截图软件进行截图。\"}},{\"score\":0.007881,\"root\":\"非自然图像-书籍封面\",\"keyword\":\"书籍\",\"baike_info\":{\"baike_url\":\"http:\\/\\/baike.baidu.com\\/item\\/%E4%B9%A6%E7%B1%8D\\/59503\",\"image_url\":\"http:\\/\\/imgsrc.baidu.com\\/baike\\/pic\\/item\\/0b46f21fbe096b63a940b6e806338744ebf8ac29.jpg\",\"description\":\"书籍，是指装订成册的图书和文字，在狭义上的理解是带有文字,图像的纸张的集合。广义的书则是一切传播信息的媒体。不过有些人认为图书一词是“河图”与“洛书”的简称。\"}}],\"log_id\":3756661021497145142,\"result_num\":5}', '2019-01-22 14:54:38', '1');

-- ----------------------------
-- Table structure for place
-- ----------------------------
DROP TABLE IF EXISTS `place`;
CREATE TABLE `place`  (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `address` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '地址',
  `phone` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '电话',
  `uid` varchar(30) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `showtag` varchar(10) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '景点级别（如5A） 餐馆类型（如中餐馆）',
  `tags1` text CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '标签',
  `image` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '列表图片',
  `word_list` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '文字推荐菜',
  `img_list` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '有图推荐菜',
  `shop_hours` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '营业时间',
  `content` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '印象标签',
  `comment_list` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '部分评论',
  `comment_number` int(10) NULL DEFAULT NULL COMMENT '评论数量',
  `photo_list` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '列表图片(相册)',
  `introduce` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '介绍',
  `latitude` double(23, 20) NULL DEFAULT NULL COMMENT '维度（前端传值）',
  `longitude` double(23, 20) NULL DEFAULT NULL COMMENT '经度（前端传值）',
  `price` varchar(10) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '餐馆平均价格',
  `overall_rating` double(10, 2) NOT NULL COMMENT '评分',
  `sug_time` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '建议游玩时间（景点）',
  `best_time` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '最佳游玩季节（景点）',
  `weather` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '天气（景点）',
  `count` int(11) NULL DEFAULT NULL COMMENT '餐馆推荐菜数',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 27 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

